import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { TextField, MenuItem, IconButton } from "@mui/material";
import ArrowBackIosNewIcon from "@mui/icons-material/ArrowBackIosNew";
import HamburgerMenu from "./HamburgerMenu"; 
import MenuIcon from "@mui/icons-material/Menu";

function ModeSelectionFlow() {
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();
  const [step, setStep] = useState(0);
  const [form, setForm] = useState({
    mode: "",
    name: "",
    gender: "",
    age: "",
    job: "",
    emotion: ""
  });

  const handleChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  const stepTitles = ["Ï∞∏Í∞Ä Î∞©Ïãù ÏÑ†ÌÉù", "Í∏∞Î≥∏ Ï†ïÎ≥¥ ÏûÖÎ†•", "Í∞êÏ†ï ÏÉÅÌÉú ÏÑ†ÌÉù"];

  const steps = [
    {
      label: "Ï∞∏Í∞ÄÎ∞©Ïãù",
      content: (
        <>
          <p>Î∞∏Î•òÎäî Í∑∏Î£π ¬∑ Í∞úÏù∏Ïù¥ Î™®Îëê Í∞ÄÎä•Ìïú ÏõåÌÅ¨ÏàçÏù¥ÏóêÏöî.<br />Ï∞∏Í∞Ä Î∞©ÏãùÏùÑ ÏÑ†ÌÉùÌï¥ Ï£ºÏÑ∏Ïöî.</p>
          <div
            style={{
              ...cardStyle,
              opacity: 0.4,
              cursor: "not-allowed",
              height: "90px",
              display: "flex",
              flexDirection: "column",
              justifyContent: "center"
            }}
          >
            <span style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>üë§</span>
            <strong>Ï†ÑÎ¨∏Í∞Ä ÏóÜÏù¥ ÌòºÏûêÌï¥Ïöî.</strong>
          </div>

          <div
            onClick={() => handleChange("mode", "group")}
            style={{
              ...cardStyle,
              border: form.mode === "group" ? "2px solid #00A6E9" : "1px solid #ccc",
              backgroundColor: form.mode === "group" ? "#f0faff" : "#fff",
              color: form.mode === "group" ? "#00A6E9" : "#444",
              height: "90px",
              display: "flex",
              flexDirection: "column",
              justifyContent: "center"
            }}
          >
            <span style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>üë•</span>
            <strong>Ï†ÑÎ¨∏Í∞ÄÏôÄ Ìï®ÍªòÌï¥Ïöî.</strong>
          </div>
        </>
      )
    },
    {
      label: "Í∏∞Î≥∏Ï†ïÎ≥¥",
      content: (
        <>
          <p>Ïù¥Î¶ÑÏùÄ ÌïÑÏàòÏù¥Î©∞, ÎÇòÎ®∏ÏßÄ Ìï≠Î™©ÏùÄ ÏÑ†ÌÉùÏûÖÎãàÎã§.<br />Îçî Íµ¨Ï≤¥Ï†ÅÏúºÎ°ú ÏûÖÎ†•Ìï†ÏàòÎ°ù Í≤∞Í≥ºÎ¨ºÏù¥ Îçî Ï†ïÍµêÌï¥Ï†∏Ïöî.</p>

          <TextField
            label="Ïù¥Î¶Ñ *"
            required
            fullWidth
            value={form.name}
            onChange={e => handleChange("name", e.target.value)}
            sx={{ mb: 2 }}
          />

          <TextField
            select
            label="ÏÑ±Î≥Ñ"
            fullWidth
            value={form.gender}
            onChange={e => handleChange("gender", e.target.value)}
            sx={{ mb: 2 }}
          >
            <MenuItem value="">ÏÑ†ÌÉù Ïïà Ìï®</MenuItem>
            <MenuItem value="ÎÇ®ÏÑ±">ÎÇ®ÏÑ±</MenuItem>
            <MenuItem value="Ïó¨ÏÑ±">Ïó¨ÏÑ±</MenuItem>
            <MenuItem value="Í∏∞ÌÉÄ">Í∏∞ÌÉÄ</MenuItem>
          </TextField>

          <TextField
            select
            label="ÎÇòÏù¥"
            fullWidth
            value={form.age}
            onChange={e => handleChange("age", e.target.value)}
            sx={{ mb: 2 }}
          >
            <MenuItem value="">ÏÑ†ÌÉù Ïïà Ìï®</MenuItem>
            <MenuItem value="10ÎåÄ">10ÎåÄ</MenuItem>
            <MenuItem value="20ÎåÄ">20ÎåÄ</MenuItem>
            <MenuItem value="30ÎåÄ">30ÎåÄ</MenuItem>
            <MenuItem value="40ÎåÄ">40ÎåÄ</MenuItem>
            <MenuItem value="50ÎåÄ Ïù¥ÏÉÅ">50ÎåÄ Ïù¥ÏÉÅ</MenuItem>
          </TextField>

          <TextField
            label="ÏßÅÏóÖ"
            fullWidth
            value={form.job}
            onChange={e => handleChange("job", e.target.value)}
          />
        </>
      )
    },
    {
      label: "Í∞êÏ†ïÏÉÅÌÉú",
      content: (
        <>
          <p>ÏßÄÎÇú 1Í∞úÏõî ÎèôÏïà,<br />Í∞ÄÏû• Í∞ÄÍπåÏö¥ ÏÉÅÌÉúÏùò Î¨∏Ïû•ÏùÑ Ìïú Í∞ÄÏßÄ Í≥®ÎùºÏ£ºÏÑ∏Ïöî.</p>
          {[
            {
              label: "Ïö∞Ïö∏Ìïú",
              description: "ÏöîÏ¶ò Ï°∞Í∏à Î¨¥Í∏∞Î†•ÌïòÍ≥†, Í¥úÌûà Ï∂ï Ï≤òÏßà ÎïåÍ∞Ä ÎßéÏïÑÏöî."
            },
            {
              label: "Î∂àÏïàÌïú",
              description: "ÏûêÍæ∏ Í±±Ï†ïÏù¥ Îñ†Ïò§Î•¥Í≥†, ÎßàÏùåÏù¥ Ïûò Í∞ÄÎùºÏïâÏßÄ ÏïäÏïÑÏöî."
            },
            {
              label: "Ïä§Ìä∏Î†àÏä§ ÎßéÏùÄ",
              description: "ÏöîÏ¶ò Î®∏Î¶øÏÜçÏù¥ Î≥µÏû°ÌïòÍ≥†, Ïâ¨Ïñ¥ÎèÑ Ïâ∞ Í≤É Í∞ôÏßÄ ÏïäÏïÑÏöî."
            },
            {
              label: "ÏûòÎ™®Î•¥Í≤†Ïñ¥Ïöî",
              description: "ÎÇ¥ ÏÉÅÌÉúÎ•º Ïûò Î™®Î•¥Í≤†Ïñ¥Ïöî."
            }
          ].map(({ label, description }) => (
            <div
              key={label}
              onClick={() => handleChange("emotion", label)}
              style={{
                border: form.emotion === label ? "2px solid #00A6E9" : "1px solid #ccc",
                backgroundColor: form.emotion === label ? "#f0faff" : "#fff",
                color: form.emotion === label ? "#00A6E9" : "#444",
                padding: "1.5rem",
                margin: "1rem 0",
                borderRadius: "12px",
                cursor: "pointer",
                fontWeight: "bold",
                textAlign: "center"
              }}
            >
              <div style={{ fontSize: "1.1rem", marginBottom: "0.5rem" }}>{label}</div>
              <div style={{ fontWeight: "normal" }}>{description}</div>
            </div>
          ))}
        </>
      )
    }
  ];

  const progressHeader = (
    <div style={{
      display: "flex",
      justifyContent: "space-around",
      alignItems: "center",
      borderBottom: "1px solid #ddd",
      marginBottom: "1.5rem",
      paddingBottom: "0.5rem"
    }}>
      {steps.map((s, idx) => {
        const isActive = step === idx;
        return (
          <div key={idx} style={{ textAlign: "center", flex: 1 }}>
            <div style={{
              width: "24px",
              height: "24px",
              borderRadius: "50%",
              backgroundColor: isActive ? "#000" : "#aaa",
              color: "#fff",
              display: "inline-flex",
              justifyContent: "center",
              alignItems: "center",
              fontSize: "0.85rem",
              fontWeight: "bold",
              marginBottom: "0.3rem"
            }}>
              {idx + 1}
            </div>
            <div style={{
              fontWeight: isActive ? "bold" : "normal",
              color: isActive ? "#000" : "#999",
              fontSize: "0.95rem"
            }}>
              {s.label}
            </div>
            {isActive && (
              <div style={{ height: "3px", backgroundColor: "#000", marginTop: "0.4rem" }} />
            )}
          </div>
        );
      })}
    </div>
  );

  const canGoNext = () => {
    if (step === 0) return form.mode !== "";
    if (step === 1) return form.name.trim() !== "";
    return true;
  };

  return (
    <div style={{ padding: "1.5rem", maxWidth: "500px", margin: "0 auto", position: "relative" }}>
      {/* ÏÉÅÎã® Ìó§Îçî */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "1rem" }}>
        <IconButton onClick={() => navigate("/consent")}>
          <ArrowBackIosNewIcon />
        </IconButton>
        <div style={{ fontSize: "1.2rem", fontWeight: "bold" }}>{stepTitles[step]}</div>
        <IconButton onClick={() => setMenuOpen(true)}>
        <MenuIcon />
      </IconButton>
      </div>

      {progressHeader}
      {steps[step].content}

      <div style={{ marginTop: "2rem", display: "flex", justifyContent: "center", gap: "1rem" }}>
        {step > 0 && (
          <button onClick={() => setStep(step - 1)} style={largeWhiteButton}>Ïù¥Ï†ÑÏúºÎ°ú</button>
        )}
        <button
          onClick={() => step < steps.length - 1 ? setStep(step + 1) : navigate("/valueintro", { state: { name: form.name } })}
          style={largeBlueButton}
          disabled={!canGoNext()}
        >
          Îã§ÏùåÏúºÎ°ú
        </button>
      </div>
      <HamburgerMenu open={menuOpen} onClose={() => setMenuOpen(false)} navigate={navigate} />
    </div>
  );
}

const cardStyle = {
  borderRadius: "12px",
  padding: "2rem",
  margin: "1rem 0",
  textAlign: "center",
  cursor: "pointer",
  boxShadow: "0 1px 4px rgba(0,0,0,0.06)",
  transition: "all 0.2s ease"
};

const largeWhiteButton = {
  flex: 1,
  height: "56px",
  fontSize: "1rem",
  backgroundColor: "#fff",
  border: "1px solid #0097D8",
  color: "#0097D8",
  borderRadius: "12px",
  fontWeight: "bold",
  cursor: "pointer"
};

const largeBlueButton = {
  flex: 1,
  height: "56px",
  fontSize: "1rem",
  backgroundColor: "#0097D8",
  border: "none",
  color: "#fff",
  borderRadius: "12px",
  fontWeight: "bold",
  cursor: "pointer"
};

export default ModeSelectionFlow;
