// Full layout update: unified with other pages (title, hamburger, styling)
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import ResultPage from "./ResultPage";
import MenuIcon from "@mui/icons-material/Menu";
import { IconButton } from "@mui/material";

const questions = [
  "가족 (부부관계나 자녀양육 제외)",
  "부부관계/친밀한 관계",
  "부모됨/양육하기",
  "친구관계",
  "일",
  "자기 자신에 대한 교육/훈련",
  "휴식/즐거운 활동",
  "영성/초월성",
  "사회참여/시민의식",
  "자신을 신체적으로 돌보기 (운동, 수면, 식이 등)",
  "환경문제",
  "예술, 창조성 등"
];

function Container({ children }) {
  return (
    <div style={{
      maxWidth: '800px',
      margin: '0 auto',
      padding: '1rem 1rem 4rem',
      backgroundColor: '#fff'
    }}>
      {children}
    </div>
  );
}

function Header() {
  return (
    <div style={{
      display: 'flex', alignItems: 'center', justifyContent: 'space-between',
      padding: '1rem 1.2rem', borderBottom: '1px solid #ddd',
      position: 'sticky', top: 0, backgroundColor: '#fff', zIndex: 100
    }}>
      <div style={{ fontSize: '1.2rem', fontWeight: 'bold' }}>내 삶의 방향 찾기</div>
      <IconButton size="small">
        <MenuIcon />
      </IconButton>
    </div>
  );
}

function QuestionSection({ title, step, responses, setResponses, onNext, onBack, disabledIndices = [] }) {
  const handleChange = (index, value) => {
    const updated = [...responses];
    updated[index] = parseInt(value);
    setResponses(updated);
  };

  const isImportance = step === 1;
  const labelLeft = isImportance ? "1 중요하지 않음" : "1 헌신하지 않음";
  const labelRight = isImportance ? "10 매우 중요" : "10 매우 헌신";

  return (
    <>
      <Header />
      <div style={{
        position: 'sticky', top: 56, backgroundColor: '#fff', zIndex: 10,
        padding: '1rem 1.2rem', textAlign: 'center', borderBottom: '1px solid #ccc'
      }}>
        <h2 style={{ fontSize: '1rem', fontWeight: 700, color: '#1292EE', margin: 0 }}>{title}</h2>
        <p style={{ fontSize: '0.85rem', color: '#555', marginTop: '0.5rem' }}>
          사람마다 중요하게 여기는 가치가 다르며, 여기에 '정답'은 없습니다. 가장 중요하다고 생각되면 10, 중요하지 않다고 생각되면 1입니다.
        </p>
      </div>
      <Container>
        <ul style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
          {questions.map((q, idx) => {
            const isDisabled = disabledIndices.includes(idx);
            return (
              <li key={idx} style={{ borderBottom: '1px solid #eee', paddingBottom: '1rem' }}>
                <div style={{ marginBottom: '0.5rem', fontWeight: 500, color: isDisabled ? '#999' : '#000' }}>{`${idx + 1}. ${q}`}</div>
                {isDisabled ? (
                  <div style={{ fontStyle: 'italic', color: '#aaa' }}>해당 없음으로 선택된 항목입니다.</div>
                ) : (
                  <>
                    <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '0.75rem', color: '#888' }}>
                      <span>{labelLeft}</span><span>{labelRight}</span>
                    </div>
                    <div style={{ position: 'relative', height: '20px', margin: '1rem 0' }}>
                      <div style={{
                        position: 'absolute',
                        top: '50%',
                        left: 0,
                        width: '100%',
                        height: '8px',
                        backgroundColor: '#eee',
                        borderRadius: '4px',
                        transform: 'translateY(-50%)'
                      }}></div>
                      <div style={{
                        position: 'absolute',
                        top: '50%',
                        left: 0,
                        width: `${(responses[idx] ?? 0) * 10}%`,
                        height: '8px',
                        backgroundColor: '#1292EE',
                        borderRadius: '4px',
                        transform: 'translateY(-50%)',
                        transition: 'width 0.3s'
                      }}></div>
                    </div>
                    <div style={{ display: 'flex', justifyContent: 'space-between', padding: '0 0.2rem' }}>
                      {[...Array(10)].map((_, i) => {
                        const score = i + 1;
                        return (
                          <label key={score} style={{ position: 'relative' }}>
                            {responses[idx] === score && (
                              <div style={{
                                position: 'absolute',
                                top: '-30px',
                                left: '50%',
                                transform: 'translateX(-50%)',
                                backgroundColor: '#1292EE',
                                color: '#fff',
                                padding: '0.2rem 0.5rem',
                                borderRadius: '8px',
                                fontSize: '0.75rem'
                              }}>{score}</div>
                            )}
                            <input
                              type="radio"
                              name={`q-${idx}`}
                              value={score}
                              checked={responses[idx] === score}
                              onChange={() => handleChange(idx, score)}
                              style={{ margin: '0 4px' }}
                            />
                          </label>
                        );
                      })}
                    </div>
                  </>
                )}
              </li>
            );
          })}
        </ul>
        <div style={{ marginTop: '3rem', textAlign: 'center' }}>
          {onBack && (
            <button
              onClick={onBack}
              style={{
                marginRight: '1rem',
                backgroundColor: '#fff',
                color: '#1292EE',
                padding: '0.6rem 1.8rem',
                borderRadius: '8px',
                fontSize: '1rem',
                border: '1px solid #1292EE'
              }}>
              이전
            </button>
          )}
          <button
            onClick={onNext}
            style={{
              backgroundColor: '#1292EE',
              color: 'white',
              padding: '0.6rem 1.8rem',
              borderRadius: '8px',
              fontSize: '1rem',
              border: 'none'
            }}>
            다음
          </button>
        </div>
      </Container>
    </>
  );
}

function calculateResults(importance, commitment) {
  const validImportance = importance.map(v => v ?? 0);
  const validCommitment = commitment.map(v => v ?? 0);

  const scored = questions.map((q, i) => ({
    label: q,
    importance: validImportance[i],
    commitment: validCommitment[i],
    index: i
  }));

  const feedback1 = scored.filter(item => item.importance >= 9).sort((a, b) => b.importance - a.importance).map(item => item.label);
  const feedback2 = scored.filter(item => item.importance >= 9 && item.commitment <= 6).sort((a, b) => b.importance - a.importance).map(item => item.label);
  const score = scored.reduce((sum, val) => sum + val.importance * val.commitment, 0) / 12;

  return { feedback1, feedback2, score };
}

function App() {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [importance, setImportance] = useState(Array(12).fill(undefined));
  const [commitment, setCommitment] = useState(Array(12).fill(undefined));
  const [results, setResults] = useState(null);

   const handleNext = () => {
    setStep(2);
    window.scrollTo({ top: 0, behavior: 'smooth' }); // ✅ 추가된 라인
  };
  const handleSubmit = () => {
    const res = calculateResults(
      importance.map(v => v ?? 0),
      commitment.map(v => v ?? 0)
    );

    navigate("/result", {
      state: {
        results: res,
        importance,
        commitment,
        // name: userName || "사용자"  // userName은 props로 전달받거나 useState로 받은 값이어야 함
        name: "사용자"
      }
    });
  };

  const handleReset = () => {
    setImportance(Array(12).fill(undefined));
    setCommitment(Array(12).fill(undefined));
    setResults(null);
    setStep(1);
  };

  return (
    <div style={{ backgroundColor: '#f8f8f8', minHeight: '100vh', padding: '4rem 2rem' }}>
      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <h1 style={{ fontSize: '2.2rem', fontWeight: 'bold', color: '#333' }}>
          가치 명료화 설문
        </h1>
      </div>

      {step === 1 && (
        <QuestionSection
          title="내가 가치롭게 여기는 정도"
          step={step}
          responses={importance}
          setResponses={setImportance}
          onNext={handleNext}
          onBack={() => navigate("/valueintro")}
        />
      )}

      {step === 2 && (
        <QuestionSection
          title="내가 헌신(행동)하는 정도"
          step={step}
          responses={commitment}
          setResponses={setCommitment}
          onNext={handleSubmit}
          onBack={() => setStep(1)}
          disabledIndices={importance.map((v, i) => v === 0 && (i === 0 || i === 2) ? i : null).filter(i => i !== null)}
        />
      )}

      {step === 3 && results && (
        <ResultPage
          results={results}
          importance={importance}
          commitment={commitment}
          onReset={handleReset}
        />
      )}
    </div>
  );
}

export default App;
