import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { Radar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  RadialLinearScale,
  PointElement,
  LineElement,
  Filler,
  Tooltip,
  Legend
} from "chart.js";
import {
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Typography,
  LinearProgress
} from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import Header from "./Header"; // ✅ 공통 헤더 컴포넌트 임포트

ChartJS.register(RadialLinearScale, PointElement, LineElement, Filler, Tooltip, Legend);

function DetailedBreakdown({ importance, commitment }) {
  const labels = [
    "태어난 가족 (부모님, 형제 등)",
    "친밀한 관계 (부부, 연인, 배우자 등)",
    "양육하기·돌보기·부모되기",
    "친구관계,사회생활",
    "일·직업·커리어",
    "배우기·자신에 대한 교육 및 훈련",
    "휴식·즐거운 활동 및 취미",
    "종교 혹은 영적활동",
    "사회에 참여하기·시민의식",
    "자신을 신체적으로 돌보기 (운동, 수면, 식이 등)",
    "환경문제",
    "예술·창조성"
  ];

  return (
    <Accordion style={{ marginTop: "2rem" }}>
      <AccordionSummary expandIcon={<ExpandMoreIcon />}>
        <Typography style={{ fontWeight: "bold", color: "#1292EE" }}>
          가치 실천 자세히 보기
        </Typography>
      </AccordionSummary>
      <AccordionDetails>
        <div style={{ display: "flex", flexDirection: "column", gap: "1.5rem" }}>
          {labels.map((label, idx) => (
            <div key={idx}>
              <Typography style={{ fontWeight: 600, marginBottom: "0.5rem" }}>
                {`${idx + 1}. ${label}`}
              </Typography>

              <Typography variant="body2" style={{ marginBottom: "0.25rem", color: "#888" }}>
                가치 ({importance[idx] ?? 0})
              </Typography>
              <LinearProgress
                variant="determinate"
                value={(importance[idx] ?? 0) * 10}
                sx={{
                  height: 10,
                  borderRadius: 5,
                  backgroundColor: "#eee",
                  "& .MuiLinearProgress-bar": {
                    backgroundColor: "#1292EE"
                  }
                }}
              />

              <Typography variant="body2" style={{ marginTop: "0.75rem", marginBottom: "0.25rem", color: "#888" }}>
                실천 ({commitment[idx] ?? 0})
              </Typography>
              <LinearProgress
                variant="determinate"
                value={(commitment[idx] ?? 0) * 10}
                sx={{
                  height: 10,
                  borderRadius: 5,
                  backgroundColor: "#eee",
                  "& .MuiLinearProgress-bar": {
                    backgroundColor: "#FF3366"
                  }
                }}
              />
            </div>
          ))}
        </div>
      </AccordionDetails>
    </Accordion>
  );
}

function ResultPage() {
  const location = useLocation();
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);

  const { results, importance, commitment, name = "사용자" } = location.state || {};

  useEffect(() => {
    if (!results || !importance || !commitment) {
      navigate("/survey");
    }
  }, [results, importance, commitment, navigate]);

  if (!results || !importance || !commitment) return null;

  const topValues = results.feedback1.slice(0, 2);
  const topValue = topValues[0] || "해당 없음";
  const lowCommit = results.feedback2.length > 0 ? results.feedback2[0] : "해당 없음";

  const labels = [
    "가족", "부부관계", "부모됨", "친구관계",
    "일", "교육", "즐거움", "영성", "사회참여", "신체관리", "환경", "창조성"
  ];

  const chartData = {
    labels,
    datasets: [
      {
        label: "가치",
        data: importance,
        backgroundColor: "rgba(18,146,238,0.15)",
        borderColor: "#1292EE",
        borderWidth: 2
      },
      {
        label: "실천",
        data: commitment,
        backgroundColor: "rgba(255,99,132,0.15)",
        borderColor: "#FF3366",
        borderWidth: 2
      }
    ]
  };

  const chartOptions = {
    responsive: true,
    scales: {
      r: {
        min: 0,
        max: 10,
        ticks: { stepSize: 2, color: "#888", font: { size: 10 } },
        pointLabels: { font: { size: 12 }, color: "#333" },
        grid: { color: "#ccc" }
      }
    },
    plugins: {
      legend: {
        display: true,
        labels: {
          color: "#444",
          font: { size: 13 }
        }
      }
    }
  };

  const goToSelection = () => {
    navigate("/stepselection", {
      state: {
        name,
        topValues
      }
    });
  };

  const goBack = () => {
    navigate("/survey");
  };

  return (
    <div style={{ backgroundColor: "#fff", minHeight: "100vh" }}>
      {/* ✅ 공통 헤더 */}
      <Header title="내 삶의 방향 찾기" setMenuOpen={setMenuOpen} />
      {/* 콘텐츠 영역 */}
      <div style={{ maxWidth: "500px", margin: "0 auto", padding: "1.5rem", fontFamily: "sans-serif" }}>
        {/* 타이틀 박스 */}
        <div style={{
          backgroundColor: "#f0f9ff",
          border: "1px solid #cce9ff",
          color: "#1292EE",
          borderRadius: "16px",
          padding: "0.8rem",
          fontWeight: "bold",
          textAlign: "center",
          marginBottom: "1.5rem"
        }}>
          나의 가치로운 삶 결과
        </div>

        {/* 레이더 차트 */}
        <Radar data={chartData} options={chartOptions} />

        {/* 점수 + 해석 */}
        <div style={{
          backgroundColor: "#f9f9f9",
          padding: "1.5rem",
          marginTop: "2rem",
          borderRadius: "16px",
          textAlign: "center",
          boxShadow: "0 0 10px rgba(0,0,0,0.04)"
        }}>
          <h3 style={{ color: "#1292EE", marginBottom: "0.5rem" }}>내 가치로운 삶 점수</h3>
          <div style={{
            fontSize: "2.5rem",
            fontWeight: "bold",
            color: "#333",
            backgroundColor: "#fff",
            display: "inline-block",
            padding: "0.5rem 1rem",
            borderRadius: "12px",
            border: "1px solid #e0e0e0",
            marginBottom: "1rem"
          }}>
            {results.score.toFixed(1)}
          </div>
          <p style={{ fontSize: "1rem", color: "#333" }}>
            당신에게 중요한 가치는 <span style={{ color: "#1292EE", fontWeight: "bold" }}>{topValue}</span>입니다.
          </p>
          <p style={{ fontSize: "1rem", color: "#333" }}>
            중요하지만 잘 실천하지 못하는 가치는 <span style={{ color: "#1292EE", fontWeight: "bold" }}>{lowCommit}</span>입니다. <br />
            {lowCommit === "없습니다" ? "모두 잘 실천하고 있는 편이네요." : ""}
          </p>
        </div>
        <DetailedBreakdown importance={importance} commitment={commitment} />
        {/* 버튼 */}
        <div style={{ display: "flex", justifyContent: "space-between", marginTop: "2rem" }}>
          <button
            onClick={goBack}
            style={{
              flex: 1,
              marginRight: "0.5rem",
              backgroundColor: "#fff",
              color: "#1292EE",
              padding: "0.7rem",
              borderRadius: "8px",
              fontSize: "1rem",
              border: "1px solid #1292EE"
            }}
          >
            이전
          </button>
          <button
            onClick={goToSelection}
            style={{
              flex: 1,
              marginLeft: "0.5rem",
              backgroundColor: "#1292EE",
              color: "#fff",
              padding: "0.7rem",
              borderRadius: "8px",
              fontSize: "1rem",
              border: "none"
            }}
          >
            다음
          </button>
        </div>
      </div>
