import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function SurveyFlow() {
  const navigate = useNavigate();
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [responses, setResponses] = useState(Array(12).fill(null));

  const questions = [
    "가족 (부부관계나 자녀양육 제외)",
    "부부관계/친밀한 관계",
    "부모됨/양육하기",
    "친구관계",
    "일",
    "자기 자신에 대한 교육/훈련",
    "휴식/즐거운 활동",
    "영성/초월성",
    "사회참여/시민의식",
    "자신을 신체적으로 돌보기 (운동, 수면, 식이 등)",
    "환경문제",
    "예술, 창조성 등"
  ];

  const handleChange = (idx, score) => {
    const updated = [...responses];
    updated[idx] = score;
    setResponses(updated);
  };

  const handlePrev = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(currentQuestion - 1);
    } else {
      navigate("/valueintro");
    }
  };

  const handleNext = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      alert("설문이 완료되었습니다!");
    }
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h2 style={{ marginBottom: "1rem" }}>내가 가치롭게 여기는 정도</h2>
      <p>{questions[currentQuestion]}</p>

      <div style={{ margin: "2rem 0", position: "relative" }}>
        <div style={{
          height: "8px",
          backgroundColor: "#eee",
          borderRadius: "4px",
          position: "relative"
        }}>
          <div style={{
            height: "8px",
            width: `${(responses[currentQuestion] ?? 0) * 10}%`,
            backgroundColor: "#aaa",
            borderRadius: "4px",
            transition: "width 0.3s"
          }} />
        </div>
        <div style={{ display: "flex", justifyContent: "space-between", marginTop: "1rem" }}>
          {Array.from({ length: 10 }, (_, i) => i + 1).map(score => (
            <div key={score} style={{ position: "relative", display: "inline-block" }}>
              {responses[currentQuestion] === score && (
                <div style={{
                  position: "absolute",
                  top: "-30px",
                  left: "50%",
                  transform: "translateX(-50%)",
                  backgroundColor: "#333",
                  color: "#fff",
                  padding: "0.2rem 0.5rem",
                  borderRadius: "8px",
                  fontSize: "0.75rem",
                  whiteSpace: "nowrap"
                }}>
                  {score}
                </div>
              )}
              <input
                type="radio"
                name={`q-${currentQuestion}`}
                value={score}
                checked={responses[currentQuestion] === score}
                onChange={() => handleChange(currentQuestion, score)}
              />
            </div>
          ))}
        </div>
      </div>

      <div style={{ marginTop: "2rem", display: "flex", justifyContent: "space-between" }}>
        <button onClick={handlePrev} style={whiteButton}>이전</button>
        <button onClick={handleNext} style={blackButton}>다음</button>
      </div>
    </div>
  );
}

const whiteButton = {
  padding: "0.8rem 2rem",
  border: "1px solid black",
  borderRadius: "12px",
  backgroundColor: "white",
  color: "black",
  fontSize: "1rem",
  cursor: "pointer"
};

const blackButton = {
  padding: "0.8rem 2rem",
  border: "none",
  borderRadius: "12px",
  backgroundColor: "black",
  color: "white",
  fontSize: "1rem",
  cursor: "pointer"
};

export default SurveyFlow;
