import React from "react";

const contentMap = {
  terms: "제1조. 목적\n이 약관은 사용자와 ‘valoo’에서 제공하는 심리 워크시트 및 관련 서비스의 이용에 있어 필요한 조건과 책임을 규정합니다.\n\n제2조. 서비스 내용\nvaloo는 사용자의 감정 및 삶의 방향을 탐색할 수 있도록 돕는 워크시트형 인터페이스를 제공합니다. ...",
  privacy: "개인정보 처리방침 내용입니다.\n\n수집 항목, 이용 목적, 보유 기간 등...",
  sensitive: "민감정보 수집 및 이용 동의 내용입니다.\n\n건강 정보, 심리상태 등 민감정보 항목 포함...",
};

function AgreementModal({ type, onClose }) {
  return (
    <div style={{
      position: "fixed",
      top: 0,
      left: 0,
      width: "100%",
      height: "100%",
      backgroundColor: "#fff",
      zIndex: 9999,
      display: "flex",
      flexDirection: "column"
    }}>
      {/* 헤더 */}
      <div style={{
        padding: "1rem",
        borderBottom: "1px solid #eee",
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center"
      }}>
        <h3 style={{ margin: 0, fontSize: "1.1rem" }}>
          {type === "terms" ? "이용약관" : type === "privacy" ? "개인정보 처리방침" : "민감정보 동의"}
        </h3>
        <button onClick={onClose} style={{ fontSize: "1.2rem", background: "none", border: "none" }}>✕</button>
      </div>

      {/* 본문 */}
      <div style={{
        padding: "1rem",
        overflowY: "scroll",
        flexGrow: 1,
        fontSize: "0.95rem",
        lineHeight: "1.6",
        whiteSpace: "pre-wrap"
      }}>
        {contentMap[type]}
      </div>
    </div>
  );
}

export default AgreementModal;
