import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { TextField, MenuItem } from "@mui/material";

function ModeSelectionFlow() {
  const navigate = useNavigate();
  const [step, setStep] = useState(0);
  const [form, setForm] = useState({
    mode: "",
    name: "",
    gender: "",
    age: "",
    job: "",
    emotion: ""
  });

  const handleChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  const steps = [
    {
      label: "참가방식",
      content: (
        <>
          <h2>참가 방식 선택</h2>
          <p>밸류는 그룹 · 개인이 모두 가능한 워크숍이에요.<br />참가 방식을 선택해 주세요.</p>
          <div
            style={{
              ...cardStyle,
              opacity: 0.4,
              cursor: "not-allowed"
            }}
          >
            <span style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>👤</span>
            <strong>전문가 없이 혼자해요.</strong>
          </div>

          <div
            onClick={() => handleChange("mode", "group")}
            style={{
              ...cardStyle,
              border: form.mode === "group" ? "2px solid #00A6E9" : "1px solid #ccc",
              backgroundColor: form.mode === "group" ? "#f0faff" : "#fff",
              color: form.mode === "group" ? "#00A6E9" : "#444"
            }}
          >
            <span style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>👥</span>
            <strong>전문가와 함께해요.</strong>
          </div>
        </>
      )
    },
    {
      label: "기본정보",
      content: (
        <>
          <h2>기본 정보 입력</h2>
          <p>이름은 필수이며, 나머지 항목은 선택입니다.<br />더 구체적으로 입력할수록 결과물이 더 정교해져요.</p>

          <TextField
            label="이름 *"
            required
            fullWidth
            value={form.name}
            onChange={e => handleChange("name", e.target.value)}
            sx={{ mb: 2 }}
          />

          <TextField
            select
            label="성별"
            fullWidth
            value={form.gender}
            onChange={e => handleChange("gender", e.target.value)}
            sx={{ mb: 2 }}
          >
            <MenuItem value="">선택 안 함</MenuItem>
            <MenuItem value="남성">남성</MenuItem>
            <MenuItem value="여성">여성</MenuItem>
            <MenuItem value="기타">기타</MenuItem>
          </TextField>

          <TextField
            select
            label="나이"
            fullWidth
            value={form.age}
            onChange={e => handleChange("age", e.target.value)}
            sx={{ mb: 2 }}
          >
            <MenuItem value="">선택 안 함</MenuItem>
            <MenuItem value="10대">10대</MenuItem>
            <MenuItem value="20대">20대</MenuItem>
            <MenuItem value="30대">30대</MenuItem>
            <MenuItem value="40대">40대</MenuItem>
            <MenuItem value="50대 이상">50대 이상</MenuItem>
          </TextField>

          <TextField
            label="직업"
            fullWidth
            value={form.job}
            onChange={e => handleChange("job", e.target.value)}
          />
        </>
      )
    },
    {
      label: "감정상태",
      content: (
        <>
          <h2>감정 상태 선택</h2>
          <p>지난 1개월 동안,<br />가장 가까운 상태의 문장을 골라주세요.</p>
          {["기운이 없고, 예전처럼 즐거움을 느끼지 못해요. 마음이 자꾸 가라앉고, 무의미하게 느껴져요.",
            "자꾸 걱정이 떠오르고, 긴장된 마음이 쉽게 가라앉지 않아요. 앞으로가 막막하게 느껴지고, 쉽게 놀라요.",
            "생각이 너무 많고, 별일 아닌 일에도 감정이 쉽게 흔들려요. 피곤한데도 쉬는 기분이 들지 않아요."]
            .map((text, idx) => (
              <div key={idx}
                onClick={() => handleChange("emotion", text)}
                style={{
                  backgroundColor: "#f5f5f5",
                  padding: "1.5rem",
                  margin: "1rem 0",
                  cursor: "pointer",
                  borderRadius: "10px",
                  border: form.emotion === text ? "2px solid #00A6E9" : "1px solid #ccc"
                }}
              >
                “{text}”
              </div>
            ))}
        </>
      )
    }
  ];

  const progressHeader = (
    <div style={{
      display: "flex",
      justifyContent: "space-around",
      alignItems: "center",
      borderBottom: "1px solid #ddd",
      marginBottom: "1.5rem",
      paddingBottom: "0.5rem"
    }}>
      {steps.map((s, idx) => {
        const isActive = step === idx;
        return (
          <div key={idx} style={{ textAlign: "center", flex: 1 }}>
            <div style={{
              width: "24px",
              height: "24px",
              borderRadius: "50%",
              backgroundColor: isActive ? "#000" : "#aaa",
              color: "#fff",
              display: "inline-flex",
              justifyContent: "center",
              alignItems: "center",
              fontSize: "0.85rem",
              fontWeight: "bold",
              marginBottom: "0.3rem"
            }}>
              {idx + 1}
            </div>
            <div style={{
              fontWeight: isActive ? "bold" : "normal",
              color: isActive ? "#000" : "#999",
              fontSize: "0.95rem"
            }}>
              {s.label}
            </div>
            {isActive && (
              <div style={{ height: "3px", backgroundColor: "#000", marginTop: "0.4rem" }} />
            )}
          </div>
        );
      })}
    </div>
  );

  const canGoNext = () => {
    if (step === 0) return form.mode !== "";
    if (step === 1) return form.name.trim() !== "";
    return true;
  };

  return (
    <div style={{ padding: "1.5rem", maxWidth: "500px", margin: "0 auto", position: "relative" }}>
      <div style={{ position: "absolute", top: "1rem", left: "1rem", fontSize: "1.4rem", cursor: "pointer" }}>☰</div>

      {progressHeader}
      {steps[step].content}

      <div style={{ marginTop: "2rem", display: "flex", justifyContent: "center", gap: "1rem" }}>
        {step > 0 && (
          <button onClick={() => setStep(step - 1)} style={largeWhiteButton}>이전</button>
        )}
        <button
          onClick={() => step < steps.length - 1 ? setStep(step + 1) : navigate("/valueintro")}
          style={largeBlueButton}
          disabled={!canGoNext()}
        >
          다음으로
        </button>
      </div>
    </div>
  );
