import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { TextField, MenuItem } from "@mui/material";

function ModeSelectionFlow() {
  const navigate = useNavigate();
  const [step, setStep] = useState(0);
  const [form, setForm] = useState({
    mode: "",
    name: "",
    gender: "",
    age: "",
    job: "",
    emotion: ""
  });

  const handleChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  const steps = [
    {
      label: "Ï∞∏Í∞ÄÎ∞©Ïãù",
      content: (
        <>
          <h2>Ï∞∏Í∞Ä Î∞©Ïãù ÏÑ†ÌÉù</h2>
          <p>Î∞∏Î•òÎäî Í∑∏Î£π ¬∑ Í∞úÏù∏Ïù¥ Î™®Îëê Í∞ÄÎä•Ìïú ÏõåÌÅ¨ÏàçÏù¥ÏóêÏöî.<br />Ï∞∏Í∞Ä Î∞©ÏãùÏùÑ ÏÑ†ÌÉùÌï¥ Ï£ºÏÑ∏Ïöî.</p>
          <div
            style={{
              ...cardStyle,
              opacity: 0.4,
              cursor: "not-allowed"
            }}
          >
            <span style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>üë§</span>
            <strong>Ï†ÑÎ¨∏Í∞Ä ÏóÜÏù¥ ÌòºÏûêÌï¥Ïöî.</strong>
          </div>

          <div
            onClick={() => handleChange("mode", "group")}
            style={{
              ...cardStyle,
              border: form.mode === "group" ? "2px solid #00A6E9" : "1px solid #ccc",
              backgroundColor: form.mode === "group" ? "#f0faff" : "#fff",
              color: form.mode === "group" ? "#00A6E9" : "#444"
            }}
          >
            <span style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>üë•</span>
            <strong>Ï†ÑÎ¨∏Í∞ÄÏôÄ Ìï®ÍªòÌï¥Ïöî.</strong>
          </div>
        </>
      )
    },
    {
      label: "Í∏∞Î≥∏Ï†ïÎ≥¥",
      content: (
        <>
          <h2>Í∏∞Î≥∏ Ï†ïÎ≥¥ ÏûÖÎ†•</h2>
          <p>Ïù¥Î¶ÑÏùÄ ÌïÑÏàòÏù¥Î©∞, ÎÇòÎ®∏ÏßÄ Ìï≠Î™©ÏùÄ ÏÑ†ÌÉùÏûÖÎãàÎã§.<br />Îçî Íµ¨Ï≤¥Ï†ÅÏúºÎ°ú ÏûÖÎ†•Ìï†ÏàòÎ°ù Í≤∞Í≥ºÎ¨ºÏù¥ Îçî Ï†ïÍµêÌï¥Ï†∏Ïöî.</p>

          <TextField
            label="Ïù¥Î¶Ñ *"
            required
            fullWidth
            value={form.name}
            onChange={e => handleChange("name", e.target.value)}
            sx={{ mb: 2 }}
          />

          <TextField
            select
            label="ÏÑ±Î≥Ñ"
            fullWidth
            value={form.gender}
            onChange={e => handleChange("gender", e.target.value)}
            sx={{ mb: 2 }}
          >
            <MenuItem value="">ÏÑ†ÌÉù Ïïà Ìï®</MenuItem>
            <MenuItem value="ÎÇ®ÏÑ±">ÎÇ®ÏÑ±</MenuItem>
            <MenuItem value="Ïó¨ÏÑ±">Ïó¨ÏÑ±</MenuItem>
            <MenuItem value="Í∏∞ÌÉÄ">Í∏∞ÌÉÄ</MenuItem>
          </TextField>

          <TextField
            select
            label="ÎÇòÏù¥"
            fullWidth
            value={form.age}
            onChange={e => handleChange("age", e.target.value)}
            sx={{ mb: 2 }}
          >
            <MenuItem value="">ÏÑ†ÌÉù Ïïà Ìï®</MenuItem>
            <MenuItem value="10ÎåÄ">10ÎåÄ</MenuItem>
            <MenuItem value="20ÎåÄ">20ÎåÄ</MenuItem>
            <MenuItem value="30ÎåÄ">30ÎåÄ</MenuItem>
            <MenuItem value="40ÎåÄ">40ÎåÄ</MenuItem>
            <MenuItem value="50ÎåÄ Ïù¥ÏÉÅ">50ÎåÄ Ïù¥ÏÉÅ</MenuItem>
          </TextField>

          <TextField
            label="ÏßÅÏóÖ"
            fullWidth
            value={form.job}
            onChange={e => handleChange("job", e.target.value)}
          />
        </>
      )
    },
    {
      label: "Í∞êÏ†ïÏÉÅÌÉú",
      content: (
        <>
          <h2>Í∞êÏ†ï ÏÉÅÌÉú ÏÑ†ÌÉù</h2>
          <p>ÏßÄÎÇú 1Í∞úÏõî ÎèôÏïà,<br />Í∞ÄÏû• Í∞ÄÍπåÏö¥ ÏÉÅÌÉúÏùò Î¨∏Ïû•ÏùÑ Í≥®ÎùºÏ£ºÏÑ∏Ïöî.</p>
          {["Í∏∞Ïö¥Ïù¥ ÏóÜÍ≥†, ÏòàÏ†ÑÏ≤òÎüº Ï¶êÍ±∞ÏõÄÏùÑ ÎäêÎÅºÏßÄ Î™ªÌï¥Ïöî. ÎßàÏùåÏù¥ ÏûêÍæ∏ Í∞ÄÎùºÏïâÍ≥†, Î¨¥ÏùòÎØ∏ÌïòÍ≤å ÎäêÍª¥Ï†∏Ïöî.",
            "ÏûêÍæ∏ Í±±Ï†ïÏù¥ Îñ†Ïò§Î•¥Í≥†, Í∏¥Ïû•Îêú ÎßàÏùåÏù¥ ÏâΩÍ≤å Í∞ÄÎùºÏïâÏßÄ ÏïäÏïÑÏöî. ÏïûÏúºÎ°úÍ∞Ä ÎßâÎßâÌïòÍ≤å ÎäêÍª¥ÏßÄÍ≥†, ÏâΩÍ≤å ÎÜÄÎùºÏöî.",
            "ÏÉùÍ∞ÅÏù¥ ÎÑàÎ¨¥ ÎßéÍ≥†, Î≥ÑÏùº ÏïÑÎãå ÏùºÏóêÎèÑ Í∞êÏ†ïÏù¥ ÏâΩÍ≤å ÌùîÎì§Î†§Ïöî. ÌîºÍ≥§ÌïúÎç∞ÎèÑ Ïâ¨Îäî Í∏∞Î∂ÑÏù¥ Îì§ÏßÄ ÏïäÏïÑÏöî."]
            .map((text, idx) => (
              <div key={idx}
                onClick={() => handleChange("emotion", text)}
                style={{
                  backgroundColor: "#f5f5f5",
                  padding: "1.5rem",
                  margin: "1rem 0",
                  cursor: "pointer",
                  borderRadius: "10px",
                  border: form.emotion === text ? "2px solid #00A6E9" : "1px solid #ccc"
                }}
              >
                ‚Äú{text}‚Äù
              </div>
            ))}
        </>
      )
    }
  ];

  const progressHeader = (
    <div style={{
      display: "flex",
      justifyContent: "space-around",
      alignItems: "center",
      borderBottom: "1px solid #ddd",
      marginBottom: "1.5rem",
      paddingBottom: "0.5rem"
    }}>
      {steps.map((s, idx) => {
        const isActive = step === idx;
        return (
          <div key={idx} style={{ textAlign: "center", flex: 1 }}>
            <div style={{
              width: "24px",
              height: "24px",
              borderRadius: "50%",
              backgroundColor: isActive ? "#000" : "#aaa",
              color: "#fff",
              display: "inline-flex",
              justifyContent: "center",
              alignItems: "center",
              fontSize: "0.85rem",
              fontWeight: "bold",
              marginBottom: "0.3rem"
            }}>
              {idx + 1}
            </div>
            <div style={{
              fontWeight: isActive ? "bold" : "normal",
              color: isActive ? "#000" : "#999",
              fontSize: "0.95rem"
            }}>
              {s.label}
            </div>
            {isActive && (
              <div style={{ height: "3px", backgroundColor: "#000", marginTop: "0.4rem" }} />
            )}
          </div>
        );
      })}
    </div>
  );

  const canGoNext = () => {
    if (step === 0) return form.mode !== "";
    if (step === 1) return form.name.trim() !== "";
    return true;
  };

  return (
    <div style={{ padding: "1.5rem", maxWidth: "500px", margin: "0 auto", position: "relative" }}>
      <div style={{ position: "absolute", top: "1rem", left: "1rem", fontSize: "1.4rem", cursor: "pointer" }}>‚ò∞</div>

      {progressHeader}
      {steps[step].content}

      <div style={{ marginTop: "2rem", display: "flex", justifyContent: "center", gap: "1rem" }}>
        {step > 0 && (
          <button onClick={() => setStep(step - 1)} style={largeWhiteButton}>Ïù¥Ï†Ñ</button>
        )}
        <button
          onClick={() => step < steps.length - 1 ? setStep(step + 1) : navigate("/valueintro")}
          style={largeBlueButton}
          disabled={!canGoNext()}
        >
          Îã§ÏùåÏúºÎ°ú
        </button>
      </div>
    </div>
  );
}

