import React, { useState } from "react";
import ResultPage from "./ResultPage";
import Advice from "./Advice";
import ReminderImage from "./ReminderImage";

const questions = [
  "태어난 가족 (부모님, 형제 등)",
  "친밀한 관계 (부부, 연인, 배우자 등)",
  "양육하기 · 돌보기, 부모되기",
  "사회생활, 친구 관계",
  "일, 직업, 커리어",
  "자기 자신에 대한 교육/훈련",
  "휴식/즐거운 활동",
  "영성/초월성",
  "사회참여/시민의식",
  "신체적으로 나를 돌보기",
  "환경문제",
  "예술, 창조성 등"
];

function SurveyFlow() {
  const [step, setStep] = useState(1);
  const [importance, setImportance] = useState(Array(12).fill(undefined));
  const [commitment, setCommitment] = useState(Array(12).fill(undefined));
  const [results, setResults] = useState(null);

  const handleSubmit = () => {
    const scored = questions.map((q, i) => ({
      label: q,
      importance: importance[i] ?? 0,
      commitment: commitment[i] ?? 0
    }));
    const score = scored.reduce((sum, val) => sum + val.importance * val.commitment, 0) / 12;
    const feedback1 = scored.filter(item => item.importance >= 9).map(item => item.label);
    const feedback2 = scored.filter(item => item.importance >= 9 && item.commitment <= 6).map(item => item.label);
    setResults({ score, feedback1, feedback2 });
    setStep(3);
  };

  const renderSliders = (responses, setResponses, disabledIndices = []) => (
    <div>
      {questions.map((q, idx) => {
        const disabled = disabledIndices.includes(idx);
        const selectedScore = responses[idx] ?? 0;

        return (
          <div key={idx} style={{ marginBottom: "2.5rem" }}>
            <h3 style={{ fontSize: "1rem", marginBottom: "0.7rem" }}>{idx + 1}. {q}</h3>
            <div style={{ display: "flex", justifyContent: "space-between", fontSize: "0.85rem", marginBottom: "0.5rem", color: "#888" }}>
              <span>중요하지 않음</span>
              <span>매우 중요</span>
            </div>
            <div style={{ position: "relative", height: "24px" }}>
              <div style={{
                position: "absolute",
                top: "8px",
                left: "0",
                height: "8px",
                width: "100%",
                backgroundColor: "#eee",
                borderRadius: "4px"
              }} />
             <div style={{
                position: "absolute",
                top: "8px",
                left: "0",
                height: "8px",
                width: `${(selectedScore / 10) * 100}%`,
                backgroundColor: "#aaa",
                borderRadius: "4px",
                transition: "width 0.3s"
              }} />
              <div style={{ display: "flex", justifyContent: "space-between", position: "relative", zIndex: 1 }}>
                {[...Array(10)].map((_, i) => {
                  const score = i + 1;
                  const selected = selectedScore === score;
                  return (
                    <label key={score} style={{ flex: 1, textAlign: "center", cursor: disabled ? "not-allowed" : "pointer" }}>
                      <input
                        type="radio"
                        name={`q-${idx}`}
                        value={score}
                        disabled={disabled}
                        checked={selected}
                        onChange={() => {
                          const updated = [...responses];
                          updated[idx] = score;
                          setResponses(updated);
                        }}
                        style={{ display: "none" }}
                      />
                      <div style={{
                        width: "10px",
                        height: "10px",
                        borderRadius: "50%",
                        backgroundColor: selected ? "#000" : "#ccc",
                        margin: "0 auto",
                        marginTop: "2px"
                      }} />
                    </label>
                  );
                })}
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );

  const disabledIndices = importance.map((v, i) => v === 0 && (i === 0 || i === 2) ? i : null).filter(i => i !== null);

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif", maxWidth: "600px", margin: "0 auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: "2rem" }}>
        내 삶의 방향 찾기
      </h2>

      {step === 1 && (
        <>
          <div style={{ marginBottom: "2rem", fontWeight: "bold", textAlign: "center", backgroundColor: "#000", color: "#fff", padding: "0.7rem", borderRadius: "8px" }}>
            내가 가치롭게 여기는 정도
          </div>
          {renderSliders(importance, setImportance)}
          <div style={{ textAlign: "center", fontSize: "0.9rem", marginTop: "1rem", color: "#666" }}>
            가장 중요하다고 생각되면 10, 중요하지 않다고 생각되면 1입니다.
          </div>
          <div style={{ display: "flex", justifyContent: "space-between", marginTop: "2rem" }}>
            <div />
            <button onClick={() => setStep(2)} style={blackButton}>다음</button>
          </div>
        </>
      )}

      {step === 2 && (
        <>
          <div style={{ marginBottom: "2rem", fontWeight: "bold", textAlign: "center", backgroundColor: "#000", color: "#fff", padding: "0.7rem", borderRadius: "8px" }}>
            실제로 전념하는 정도
          </div>
          {renderSliders(commitment, setCommitment, disabledIndices)}
          <div style={{ display: "flex", justifyContent: "space-between", marginTop: "2rem" }}>
            <button onClick={() => setStep(1)} style={whiteButton}>이전</button>
            <button onClick={handleSubmit} style={blackButton}>결과 보기</button>
          </div>
        </>
      )}

      {step === 3 && results && (
        <ResultPage
          results={results}
          importance={importance}
          commitment={commitment}
          onNext={() => setStep(4)}
        />
      )}

      {step === 4 && (
        <Advice results={results} onBack={() => setStep(3)} onNext={() => setStep(5)} />
      )}

      {step === 5 && (
        <ReminderImage onBack={() => setStep(4)} />
      )}
    </div>
  );
}

const blackButton = {
  padding: "1rem 2rem",
  border: "none",
  borderRadius: "12px",
  backgroundColor: "black",
  color: "white",
  fontSize: "1rem",
  cursor: "pointer"
};

const whiteButton = {
  padding: "1rem 2rem",
  border: "1px solid black",
  borderRadius: "12px",
  backgroundColor: "white",
  color: "black",
  fontSize: "1rem",
  cursor: "pointer"
};

export default SurveyFlow;
