import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function ModeSelectionFlow() {
  const navigate = useNavigate();
  const [step, setStep] = useState(0);
  const [form, setForm] = useState({
    mode: "",       // "solo" | "group"
    name: "",
    gender: "",
    age: "",
    job: "",
    emotion: ""
  });

  const handleChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  const steps = [
    {
      label: "Ï∞∏Í∞ÄÎ∞©Ïãù",
      content: (
        <>
          <h2>Ï∞∏Í∞Ä Î∞©Ïãù ÏÑ†ÌÉù</h2>
          <p>Î∞∏Î•òÎäî Í∑∏Î£π ¬∑ Í∞úÏù∏Ïù¥ Î™®Îëê Í∞ÄÎä•Ìïú ÏõåÌÅ¨ÏàçÏù¥ÏóêÏöî.<br />Ï∞∏Í∞Ä Î∞©ÏãùÏùÑ ÏÑ†ÌÉùÌï¥ Ï£ºÏÑ∏Ïöî.</p>

          <div
            onClick={() => handleChange("mode", "solo")}
            style={{
              ...cardStyle,
              border: form.mode === "solo" ? "2px solid #00A6E9" : "1px solid #ccc",
              backgroundColor: form.mode === "solo" ? "#f0faff" : "#fff",
              color: form.mode === "solo" ? "#00A6E9" : "#444"
            }}
          >
            <span style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>üë§</span>
            <strong>Ï†ÑÎ¨∏Í∞Ä ÏóÜÏù¥ ÌòºÏûêÌï¥Ïöî.</strong>
          </div>

          <div
            onClick={() => handleChange("mode", "group")}
            style={{
              ...cardStyle,
              border: form.mode === "group" ? "2px solid #00A6E9" : "1px solid #ccc",
              backgroundColor: form.mode === "group" ? "#f0faff" : "#fff",
              color: form.mode === "group" ? "#00A6E9" : "#444"
            }}
          >
            <span style={{ fontSize: "2rem", marginBottom: "0.5rem" }}>üë•</span>
            <strong>Ï†ÑÎ¨∏Í∞ÄÏôÄ Ìï®ÍªòÌï¥Ïöî.</strong>
          </div>
        </>
      )
    },
    {
      label: "Í∏∞Î≥∏Ï†ïÎ≥¥",
      content: (
        <>
          <h2>Í∏∞Î≥∏ Ï†ïÎ≥¥ ÏûÖÎ†•</h2>
          <p>Ïù¥Î¶ÑÏùÄ ÌïÑÏàòÏù¥Î©∞, ÎÇòÎ®∏ÏßÄ Ìï≠Î™©ÏùÄ ÏÑ†ÌÉùÏûÖÎãàÎã§.<br />Îçî Íµ¨Ï≤¥Ï†ÅÏúºÎ°ú ÏûÖÎ†•Ìï†ÏàòÎ°ù Í≤∞Í≥ºÎ¨ºÏù¥ Îçî Ï†ïÍµêÌï¥Ï†∏Ïöî.</p>
          <input placeholder="Ïù¥Î¶Ñ *" required value={form.name} onChange={e => handleChange("name", e.target.value)} style={inputStyle} />
          <input placeholder="ÏÑ±Î≥Ñ" value={form.gender} onChange={e => handleChange("gender", e.target.value)} style={inputStyle} />
          <input placeholder="ÎÇòÏù¥" value={form.age} onChange={e => handleChange("age", e.target.value)} style={inputStyle} />
          <input placeholder="ÏßÅÏóÖ" value={form.job} onChange={e => handleChange("job", e.target.value)} style={inputStyle} />
        </>
      )
    },
    {
      label: "Í∞êÏ†ïÏÉÅÌÉú",
      content: (
        <>
          <h2>Í∞êÏ†ï ÏÉÅÌÉú ÏÑ†ÌÉù</h2>
          <p>ÏßÄÎÇú 1Í∞úÏõî ÎèôÏïà,<br />Í∞ÄÏû• Í∞ÄÍπåÏö¥ ÏÉÅÌÉúÏùò Î¨∏Ïû•ÏùÑ Í≥®ÎùºÏ£ºÏÑ∏Ïöî.</p>
          {[
            "Í∏∞Ïö¥Ïù¥ ÏóÜÍ≥†, ÏòàÏ†ÑÏ≤òÎüº Ï¶êÍ±∞ÏõÄÏùÑ ÎäêÎÅºÏßÄ Î™ªÌï¥Ïöî. ÎßàÏùåÏù¥ ÏûêÍæ∏ Í∞ÄÎùºÏïâÍ≥†, Î¨¥ÏùòÎØ∏ÌïòÍ≤å ÎäêÍª¥Ï†∏Ïöî.",
            "ÏûêÍæ∏ Í±±Ï†ïÏù¥ Îñ†Ïò§Î•¥Í≥†, Í∏¥Ïû•Îêú ÎßàÏùåÏù¥ ÏâΩÍ≤å Í∞ÄÎùºÏïâÏßÄ ÏïäÏïÑÏöî. ÏïûÏúºÎ°úÍ∞Ä ÎßâÎßâÌïòÍ≤å ÎäêÍª¥ÏßÄÍ≥†, ÏâΩÍ≤å ÎÜÄÎùºÏöî.",
            "ÏÉùÍ∞ÅÏù¥ ÎÑàÎ¨¥ ÎßéÍ≥†, Î≥ÑÏùº ÏïÑÎãå ÏùºÏóêÎèÑ Í∞êÏ†ïÏù¥ ÏâΩÍ≤å ÌùîÎì§Î†§Ïöî. ÌîºÍ≥§ÌïúÎç∞ÎèÑ Ïâ¨Îäî Í∏∞Î∂ÑÏù¥ Îì§ÏßÄ ÏïäÏïÑÏöî."
          ].map((text, idx) => (
            <div key={idx}
              onClick={() => handleChange("emotion", text)}
              style={{
                backgroundColor: "#f5f5f5",
                padding: "1.5rem",
                margin: "1rem 0",
                cursor: "pointer",
                borderRadius: "10px",
                border: form.emotion === text ? "2px solid #00A6E9" : "1px solid #ccc"
              }}
            >
              ‚Äú{text}‚Äù
            </div>
          ))}
        </>
      )
    }
  ];

  const canGoNext = () => {
    if (step === 0) return form.mode !== "";
    if (step === 1) return form.name.trim() !== "";
    return true;
  };

  return (
    <div style={{ padding: "1.5rem", maxWidth: "500px", margin: "0 auto", position: "relative" }}>
      {/* ÌñÑÎ≤ÑÍ±∞ Î©îÎâ¥ */}
      <div style={{ position: "absolute", top: "1rem", left: "1rem", fontSize: "1.4rem", cursor: "pointer" }}>‚ò∞</div>

      {/* ÏßÑÌñâ ÌëúÏãú */}
      <h3 style={{ textAlign: "center", marginBottom: "1rem" }}>{steps[step].label} ÏÑ†ÌÉù</h3>
      <div style={{ display: "flex", justifyContent: "center", gap: "1.5rem", marginBottom: "1rem" }}>
        {steps.map((s, idx) => (
          <div key={idx} style={{
            fontWeight: step === idx ? "bold" : "normal",
            color: step === idx ? "#000" : "#bbb"
          }}>
            {step === idx ? `‚óè ${s.label}` : `${idx + 1} ${s.label}`}
          </div>
        ))}
      </div>

      {/* Ïª®ÌÖêÏ∏† */}
      {steps[step].content}

      {/* Î≤ÑÌäº */}
      <div style={{ marginTop: "2rem", display: "flex", justifyContent: "space-between" }}>
        {step > 0 && (
          <button onClick={() => setStep(step - 1)} style={whiteButton}>Ïù¥Ï†Ñ</button>
        )}
        <button
          onClick={() => step < steps.length - 1 ? setStep(step + 1) : navigate("/valueintro")}
          style={blackButton}
          disabled={!canGoNext()}
        >
          Îã§Ïùå
        </button>
      </div>
    </div>
  );
}

const cardStyle = {
  borderRadius: "12px",
  padding: "2rem",
  margin: "1rem 0",
  textAlign: "center",
  cursor: "pointer",
  boxShadow: "0 1px 4px rgba(0,0,0,0.06)",
  transition: "all 0.2s ease"
};

const inputStyle = {
  width: "100%",
  padding: "1rem",
  marginBottom: "1rem",
  backgroundColor: "#f0f0f0",
  border: "none",
  borderRadius: "8px"
};

const whiteButton = {
  padding: "0.8rem 2rem",
  border: "1px solid black",
  borderRadius: "12px",
  backgroundColor: "white",
  color: "black",
  fontSize: "1rem",
  cursor: "pointer"
};

const blackButton = {
  padding: "0.8rem 2rem",
  border: "none",
  borderRadius: "12px",
  backgroundColor: "#0097D8",
  color: "white",
  fontSize: "1rem",
  cursor: "pointer"
};

export default ModeSelectionFlow;
