import React from "react";
import { Radar } from "react-chartjs-2";
import { Chart as ChartJS, RadialLinearScale, PointElement, LineElement, Filler, Tooltip, Legend } from "chart.js";
import { useNavigate } from "react-router-dom";

ChartJS.register(RadialLinearScale, PointElement, LineElement, Filler, Tooltip, Legend);

function ResultPage({ results, importance, commitment, onNext, onReset }) {
  const navigate = useNavigate(); // 내비게이션 훅
  const labels = [
    "가족", "부부관계", "부모됨", "친구관계",
    "일", "교육", "즐거움", "영성", "사회참여", "신체관리", "환경", "창조성"
  ];

  const chartData = {
    labels,
    datasets: [
      {
        label: "가치",
        data: importance,
        backgroundColor: "rgba(255,99,132,0.2)",
        borderColor: "rgba(255,99,132,1)",
        borderWidth: 1,
      },
      {
        label: "실천",
        data: commitment,
        backgroundColor: "rgba(54, 162, 235, 0.2)",
        borderColor: "rgba(54, 162, 235, 1)",
        borderWidth: 1,
      }
    ]
  };

  const chartOptions = {
    scales: {
      r: {
        min: 0,
        max: 10,
        ticks: { stepSize: 1 },
        pointLabels: { font: { size: 12 } }
      }
    },
    plugins: {
      legend: { display: false }
    }
  };

  const topValue = results.feedback1[0] || "없습니다";
  const lowCommit = results.feedback2.length > 0 ? results.feedback2[0] : "없습니다";

  return (
    <div style={{ maxWidth: "500px", margin: "0 auto", padding: "2rem", fontFamily: "sans-serif" }}>
      <h2 style={{ textAlign: "center", marginBottom: "1.5rem" }}>내 삶의 방향 찾기</h2>

      <div style={{ backgroundColor: "#000", color: "#fff", padding: "1rem", textAlign: "center", borderRadius: "8px", marginBottom: "1.5rem" }}>
        <strong>나의 가치로운 삶 결과</strong>
      </div>

      <Radar data={chartData} options={chartOptions} />

      <div style={{
        backgroundColor: "#fafafa",
        padding: "1.5rem",
        marginTop: "2rem",
        borderRadius: "12px",
        textAlign: "center",
        boxShadow: "0 0 10px rgba(0,0,0,0.05)"
      }}>
        <h3 style={{ margin: "0 0 0.8rem", fontWeight: "600", fontSize: "1.1rem" }}>내 가치로운 삶 점수</h3>
        <div style={{ fontSize: "2.5rem", fontWeight: "700", margin: "0.5rem 0 1rem" }}>{results.score.toFixed(1)}</div>

        <p style={{ fontSize: "1rem", marginBottom: "1rem" }}>
          당신에게 중요한 중요한 가치는 <strong style={{ color: "#a678ff" }}>{topValue}</strong> 입니다.
        </p>
        <p style={{ fontSize: "1rem", color: "#333" }}>
          중요하지만 잘 실천하지 못하는 가치는 <strong style={{ color: "#a678ff" }}>{lowCommit}</strong>입니다. <br />
          {lowCommit === "없습니다" ? "모두 잘 실천하고 있는 편이네요." : ""}
        </p>
      </div>

      <div style={{ textAlign: "center", marginTop: "2.5rem" }}>
        <button
          onClick={onNext}
          style={{
            backgroundColor: "#000",
            color: "#fff",
            padding: "0.7rem 2.2rem",
            fontSize: "1rem",
            border: "none",
            borderRadius: "12px",
            cursor: "pointer"
          }}
        >
          다음
        </button>
      </div>
    </div>
  );
}

export default ResultPage;
