import React, { useState } from "react";
import { FormControl, InputLabel, MenuItem, Select, Typography, Box } from "@mui/material";

export default function CommitmentDropdown() {
  const [commitment, setCommitment] = useState("");

  const handleChange = (event) => {
    setCommitment(event.target.value);
  };

  return (
    <Box sx={{ maxWidth: 300, margin: "2rem auto", fontFamily: "sans-serif" }}>
      <Typography variant="h6" gutterBottom>
        헌신하는 정도
      </Typography>
      <Typography variant="body2" sx={{ mb: 1 }}>
        각 가치에 대해, 내가 실제로 삶 속에서 얼마나 실천하고 있는지를 떠올려보세요.
        가장 헌신하고 있다면 10, 거의 실천하지 못하고 있다면 1입니다.
      </Typography>
      <FormControl fullWidth>
        <InputLabel id="commitment-label">점수 선택</InputLabel>
        <Select
          labelId="commitment-label"
          id="commitment-select"
          value={commitment}
          label="점수 선택"
          onChange={handleChange}
        >
          {[...Array(10)].map((_, i) => (
            <MenuItem key={i + 1} value={i + 1}>
              {i + 1}
            </MenuItem>
          ))}
        </Select>
      </FormControl>
      {commitment && (
        <Typography sx={{ mt: 2 }}>
          선택한 점수: <strong>{commitment}</strong>
        </Typography>
      )}
    </Box>
  );
}
