import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import MenuIcon from "@mui/icons-material/Menu";
import ArrowBackIosNewIcon from "@mui/icons-material/ArrowBackIosNew";
import { IconButton } from "@mui/material";
import nextstepImg from "./assets/nextstep.png";
import HamburgerMenu from "./HamburgerMenu";

function NextStepChoicePage() {
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const name = location.state?.name || "ÏÇ¨Ïö©Ïûê";

  const handleSelect = (path) => {
    navigate(path);
  };

  return (
    <div style={{ padding: "1.5rem", maxWidth: "500px", margin: "0 auto", position: "relative", minHeight: "100vh", display: "flex", flexDirection: "column" }}>
      {/* ÏÉÅÎã® Ìó§Îçî */}
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: "1rem" }}>
        <IconButton onClick={() => navigate("/consent")}> <ArrowBackIosNewIcon fontSize="small" /> </IconButton>
        <div style={{ fontSize: "1.2rem", fontWeight: "bold" }}>Í∞àÎ¶ºÍ∏∏</div>
        <IconButton onClick={() => setMenuOpen(true)}><MenuIcon /></IconButton>
      </div>

      {/* ÌÖçÏä§Ìä∏ */}
      <div style={{ textAlign: "center", marginBottom: "1.5rem" }}>
        <p>Í∞àÎ¶ºÍ∏∏Ïù¥ ÎÇòÌÉÄÎÇ¨Ïñ¥Ïöî.</p>
        <p style={{ color: "#00A6E9", fontWeight: "bold" }}>{name}ÎãòÏùò Îã§Ïùå Ïó¨Ï†ïÏùÑ ÏÑ†ÌÉùÌï¥Î≥¥ÏÑ∏Ïöî.</p>
      </div>

      {/* ÏÑ†ÌÉùÏßÄ Ïπ¥Îìú */}
      <div
        onClick={() => handleSelect("/valueintro")}
        style={{
          border: "1px solid #ccc",
          borderRadius: "12px",
          padding: "1.5rem",
          marginBottom: "1rem",
          cursor: "pointer",
          fontWeight: "bold",
          display: "flex",
          alignItems: "center",
          gap: "1rem",
          backgroundColor: "#fff"
        }}
      >
        <span>üìù</span>
        ÎÇ¥ Í∞ÄÏπòÎ•º Îçî Ïù¥Ìï¥Ìï¥Î≥ºÎûòÏöî.
      </div>

      <div
        onClick={() => handleSelect("/whatif")}
        style={{
          border: "2px solid #00A6E9",
          backgroundColor: "#f0faff",
          borderRadius: "12px",
          padding: "1.5rem",
          fontWeight: "bold",
          display: "flex",
          alignItems: "center",
          gap: "1rem",
          color: "#00A6E9",
          cursor: "pointer"
        }}
      >
        <span>‚ú®</span>
        ÏßÄÍ∏à ÎÇ¥ Í≥†ÎØºÏùÑ ÌÉêÏÉâÌï†ÎûòÏöî.
      </div>

      {/* Ïù¥ÎØ∏ÏßÄ ÌïòÎã® */}
      <div style={{ marginTop: "auto", paddingTop: "2rem" }}>
        <img
          src={nextstepImg} alt="next background" 
          style={{ width: "100%", objectFit: "contain" }}
        />
      </div>
       {/* ‚úÖ Ïó¨Í∏∞ ÎÑ£Í∏∞ */}
    <HamburgerMenu open={menuOpen} onClose={() => setMenuOpen(false)} navigate={navigate} />
    </div>
  );
}

export default NextStepChoicePage;
