import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import QuestionSection, { Header, ValueIntroTitle } from "./QuestionSection";
import ResultPage from "./ResultPage";

function calculateResults(importance, commitment) {
  const validImportance = importance.map(v => v ?? 0);
  const validCommitment = commitment.map(v => v ?? 0);
  const scored = validImportance.map((imp, i) => ({
    label: i + 1,
    importance: imp,
    commitment: validCommitment[i],
    index: i
  }));

  const feedback1 = scored.filter(d => d.importance >= 9).map(d => d.label);
  const feedback2 = scored.filter(d => d.importance >= 9 && d.commitment <= 6).map(d => d.label);
  const score = scored.reduce((sum, d) => sum + d.importance * d.commitment, 0) / 12;

  return { feedback1, feedback2, score };
}

 function SurveyFlow() {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [importance, setImportance] = useState(Array(12).fill(undefined));
  const [commitment, setCommitment] = useState(Array(12).fill(undefined));
  const [results, setResults] = useState(null);

  const handleNext = () => {
    setStep(2);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const handleSubmit = () => {
    const res = calculateResults(importance, commitment);
    setResults(res);
    navigate("/result", {
      state: {
        results: res,
        importance,
        commitment,
        name: "사용자"
      }
    });
  };

  const handleReset = () => {
    setImportance(Array(12).fill(undefined));
    setCommitment(Array(12).fill(undefined));
    setResults(null);
    setStep(1);
  };

  return (
    <div style={{ backgroundColor: "#fff", minHeight: "100vh" }}>
      <Header />
      <ValueIntroTitle step={step} />

      {step === 1 && (
        <QuestionSection
          step={step}
          responses={importance}
          setResponses={setImportance}
          onNext={handleNext}
          onBack={() => navigate("/valueintro")}
        />
      )}
      {step === 2 && (
        <QuestionSection
          step={step}
          responses={commitment}
          setResponses={setCommitment}
          onNext={handleSubmit}
          onBack={() => setStep(1)}
          disabledIndices={importance.map((v, i) =>
            v === 0 && (i === 0 || i === 2) ? i : null
          ).filter(i => i !== null)}
        />
      )}
      {step === 3 && results && (
        <ResultPage
          results={results}
          importance={importance}
          commitment={commitment}
          onReset={handleReset}
        />
      )}
    </div>
  );
}

export default SurveyFlow;
