import React, { useState } from "react";
import ResultPage from "./ResultPage";

const questions = [
  "가족 (부부관계나 자녀양육 제외)",
  "부부관계/친밀한 관계",
  "부모됨/양육하기",
  "친구관계",
  "일",
  "자기 자신에 대한 교육/훈련",
  "휴식/즐거운 활동",
  "영성/초월성",
  "사회참여/시민의식",
  "자신을 신체적으로 돌보기 (운동, 수면, 식이 등)",
  "환경문제",
  "예술, 창조성 등"
];

function Container({ children }) {
  return (
    <div style={{ maxWidth: '800px', margin: '2rem auto', padding: '2rem', backgroundColor: '#fff', borderRadius: '12px', boxShadow: '0 0 20px rgba(0,0,0,0.05)' }}>
      {children}
    </div>
  );
}

function QuestionSection({ title, responses, setResponses, onNext, disabledIndices = [] }) {
  const handleChange = (index, value) => {
    const updated = [...responses];
    updated[index] = parseInt(value);
    setResponses(updated);
  };

  const handleNextWithScroll = () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
    onNext();
  };

  return (
    <Container>
      <h2 style={{ fontSize: '1.3rem', fontWeight: 600, marginBottom: '2rem', textAlign: 'center', color: '#2a2a2a' }}>{title}</h2>
      <ul style={{ display: 'flex', flexDirection: 'column', gap: '2rem' }}>
        {questions.map((q, idx) => {
          const isDisabled = disabledIndices.includes(idx);
          const allowNone = idx === 0 || idx === 2;
          const selectedScore = responses[idx];

          return (
            <li key={idx} style={{ borderBottom: '1px solid #eee', paddingBottom: '1rem' }}>
              <div style={{ marginBottom: '0.5rem', fontWeight: 500, color: isDisabled ? '#999' : '#000' }}>{idx + 1}. {q}</div>
              {isDisabled ? (
                <div style={{ fontStyle: 'italic', color: '#aaa' }}>해당 없음으로 선택된 항목입니다.</div>
              ) : (
                <div style={{ position: 'relative', padding: '1.5rem 0 0.5rem' }}>
                  <div style={{ position: 'relative', height: '8px', backgroundColor: '#eee', borderRadius: '4px' }}>
                    <div style={{
                      position: 'absolute',
                      top: 0,
                      left: 0,
                      height: '8px',
                      backgroundColor: '#999',
                      width: `${(selectedScore ?? 0) * 10}%`,
                      borderRadius: '4px',
                      transition: 'width 0.3s'
                    }} />
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '1rem', position: 'relative' }}>
                    {[...Array(10)].map((_, i) => {
                      const score = i + 1;
                      const selected = selectedScore === score;
                      return (
                        <label key={score} style={{ position: 'relative', flex: 1, textAlign: 'center' }}>
                          {selected && (
                            <div style={{
                              position: 'absolute',
                              top: '-1.5rem',
                              left: '50%',
                              transform: 'translateX(-50%)',
                              background: '#333',
                              color: '#fff',
                              padding: '0.2rem 0.5rem',
                              borderRadius: '6px',
                              fontSize: '0.7rem',
                              whiteSpace: 'nowrap'
                            }}>
                              {score}
                            </div>
                          )}
                          <input
                            type="radio"
                            name={`q-${idx}`}
                            value={score}
                            checked={selected}
                            onChange={() => handleChange(idx, score)}
                            style={{ appearance: 'none', width: '12px', height: '12px', borderRadius: '50%', backgroundColor: selected ? '#000' : '#ccc', cursor: 'pointer' }}
                          />
                        </label>
                      );
                    })}
                  </div>
                </div>
              )}
            </li>
          );
        })}
      </ul>
      <div style={{ marginTop: '3rem', textAlign: 'center' }}>
        <button
          onClick={handleNextWithScroll}
          style={{ backgroundColor: '#000', color: 'white', padding: '0.7rem 2rem', borderRadius: '8px', fontSize: '1rem' }}
        >
          다음
        </button>
      </div>
    </Container>
  );
}

function calculateResults(importance, commitment) {
  const validImportance = importance.map(v => v ?? 0);
  const validCommitment = commitment.map(v => v ?? 0);

  const scored = questions.map((q, i) => ({
    label: q,
    importance: validImportance[i],
    commitment: validCommitment[i],
    index: i
  }));

  const feedback1 = scored.filter(item => item.importance >= 9).map(item => item.label);
  const feedback2 = scored.filter(item => item.importance >= 9 && item.commitment <= 6).map(item => item.label);
  const score = scored.reduce((sum, val) => sum + val.importance * val.commitment, 0) / 12;

  return { feedback1, feedback2, score };
}

function App() {
  const [step, setStep] = useState(1);
  const [importance, setImportance] = useState(Array(12).fill(undefined));
  const [commitment, setCommitment] = useState(Array(12).fill(undefined));
  const [results, setResults] = useState(null);

  const handleNext = () => setStep(2);
  const handleSubmit = () => {
    const res = calculateResults(importance, commitment);
    setResults(res);
    setStep(3);
  };

  const handleReset = () => {
    setImportance(Array(12).fill(undefined));
    setCommitment(Array(12).fill(undefined));
    setResults(null);
    setStep(1);
  };

  return (
    <div style={{ backgroundColor: '#f8f8f8', minHeight: '100vh', padding: '4rem 2rem' }}>
      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <h1 style={{ fontSize: '2.2rem', fontWeight: 'bold', color: '#333' }}>가치 명료화 설문</h1>
      </div>
      {step === 1 && (
        <QuestionSection
          title="1. 내가 가치롭게 여기는 정도"
          responses={importance}
          setResponses={setImportance}
          onNext={handleNext}
        />
      )}
      {step === 2 && (
        <QuestionSection
          title="2. 실제로 헌신하고 전념하는 정도"
          responses={commitment}
          setResponses={setCommitment}
          onNext={handleSubmit}
          disabledIndices={importance.map((v, i) => v === 0 && (i === 0 || i === 2) ? i : null).filter(i => i !== null)}
        />
      )}
      {step === 3 && results && (
        <ResultPage
          results={results}
          importance={importance}
          commitment={commitment}
          onReset={handleReset}
        />
      )}
    </div>
  );
}

export default App;
