import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import ValueIntro1 from "./assets/valueIntro1.png";
// import ValueIntro2 from "./assets/ValueIntro2.png";
// import ValueIntro3 from "./assets/ValueIntro3.png";

function ValueIntro() {
  const navigate = useNavigate();
  const [step, setStep] = useState(0);

  const pages = [
    {
      title: "내 삶의 방향 찾기",
      subtitle: "'가치'란\n우리가 세상, 타인, 그리고 자기 자신과\n어떻게 관계 맺기를 원하는지를 향한\n마음 깊은 바람입니다.\n\n가치는 우리 삶을 이끄는 원칙이자,\n삶을 살아가는 데\n동기를 부여해주는 방향성입니다.",
      image: ValueIntro1
    },
    {
      title: "내 삶의 방향 찾기",
      subtitle: "다음에 제시되는 내용은\n일부 사람들이 중요하다고 여기는\n삶의 영역입니다.\n\n모든 사람들이 동일한 가치를 가지고 있지 않으며,\n이 워크시트로 당신이 올바른 가치를 지니고\n있는지 확인하려고 하는 것도 아닙니다.",
      image: null // ValueIntro2
    },
    {
      title: "내 삶의 방향 찾기",
      subtitle: "‘아무도’ 이 워크시트를 읽지 않는다고\n생각하시고 당신의 가치를 찾아 보세요.",
      image: null // ValueIntro3
    }
  ];

  const current = pages[step];

  const handleNext = () => {
    if (step < pages.length - 1) {
      setStep(step + 1);
    } else {
      navigate("/survey");
    }
  };

  const skip = () => navigate("/survey");

  return (
    <div style={{
      padding: "2rem",
      fontFamily: "sans-serif",
      minHeight: "100vh",
      display: "flex",
      flexDirection: "column",
      justifyContent: "space-between"
    }}>
      <div>
        <h2 style={{ fontSize: "1.2rem", textAlign: "center", marginBottom: "1.5rem" }}>{current.title}</h2>
        {current.image ? (
          <img
            src={current.image}
            alt="value intro illustration"
            style={{
              width: "100%",
              maxWidth: "100%",
              borderRadius: "16px",
              marginBottom: "1.5rem"
            }}
          />
        ) : (
          <div style={{
            width: "100%",
            height: "240px",
            backgroundColor: "#eee",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            borderRadius: "16px",
            marginBottom: "1.5rem"
          }}>
            이미지 없음
          </div>
        )}
        <p style={{
          whiteSpace: "pre-line",
          fontSize: "0.95rem",
          color: "#333",
          textAlign: "center"
        }}>{current.subtitle}</p>
      </div>

      <div style={{
        marginTop: "2rem",
        display: "flex",
        justifyContent: step < 2 ? "space-between" : "center"
      }}>
        {step < 2 && (
          <button onClick={skip} style={whiteButton}>건너뛰기</button>
        )}
        <button onClick={handleNext} style={blackButton}>
          {step < 2 ? "다음으로" : "시작할게요"}
        </button>
      </div>
    </div>
  );
}

const whiteButton = {
  padding: "0.8rem 2rem",
  border: "1px solid black",
  borderRadius: "12px",
  backgroundColor: "white",
  color: "black",
  fontSize: "1rem",
  cursor: "pointer"
};

const blackButton = {
  padding: "0.8rem 2rem",
  border: "none",
  borderRadius: "12px",
  backgroundColor: "black",
  color: "white",
  fontSize: "1rem",
  cursor: "pointer"
};

export default ValueIntro;
