import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "./Header"; // ✅ 우리가 쓰는 헤더
import ValueIntro1 from "./assets/valueIntro1.png"; // ✅ 이미지 import (경로 확인 필요)

function ValueIntro() {
  const navigate = useNavigate();

  const pages = [
    {
      subtitle: (
        <>
          <div style={bubbleStyle}>
            <p><strong style={{ color: "#1292EE" }}>‘가치’란?</strong><br />
            우리가 세상, 타인, 그리고 자기 자신과<br />
            어떻게 관계 맺기를 원하는지를 향한<br />
            마음 깊은 바람입니다.</p>
          </div>
          <div style={connector}></div>
          <div style={bubbleStyleSecondary}>
            <p>
              가치는 우리 삶을 이끄는 <strong>원칙</strong>이자,<br />
              삶을 살아가는 데 동기를 부여해주는 <strong>방향성</strong>입니다.
            </p>
          </div>
        </>
      ),
      image: ValueIntro1
    }
    // 이후 step 1, 2도 이와 같이 추가 가능
  ];

  const current = pages[step];

  const handleNext = () => {
    if (step < pages.length - 1) {
      setStep(step + 1);
    } else {
      navigate("/survey");
    }
  };

  return (
    <div style={{ backgroundColor: "#f5fafe", minHeight: "100vh", fontFamily: "sans-serif" }}>
      <Header title="내 삶의 방향 찾기" />
      <div style={{ padding: "1.2rem", maxWidth: "500px", margin: "0 auto" }}>
        <div style={{ marginBottom: "1.5rem" }}>
          <img src={current.image} alt="인트로" style={{ width: "100%", borderRadius: "12px" }} />
        </div>
        <div style={{ textAlign: "center", fontSize: "0.95rem", color: "#333" }}>
          {current.subtitle}
        </div>
      </div>

      <div style={{ maxWidth: "500px", margin: "2rem auto", padding: "0 1.2rem" }}>
        <button
          onClick={handleNext}
          style={{
            width: "100%",
            padding: "1rem 0",
            fontSize: "1.1rem",
            backgroundColor: "#1292EE",
            color: "white",
            border: "none",
            borderRadius: "12px",
            fontWeight: "bold"
          }}
        >
          다음으로
        </button>
      </div>
    </div>
  );
}

// 💬 스타일
const bubbleStyle = {
  backgroundColor: "#fff",
  border: "1px solid #1292EE",
  borderRadius: "20px",
  padding: "1rem",
  marginBottom: "1rem",
  fontSize: "0.95rem",
  color: "#333",
  lineHeight: "1.5"
};

const bubbleStyleSecondary = {
  backgroundColor: "#d9efff",
  borderRadius: "16px",
  padding: "1rem",
  fontSize: "0.95rem",
  color: "#333",
  lineHeight: "1.5"
};

const connector = {
  height: "16px",
  width: "2px",
  backgroundColor: "#1292EE",
  margin: "0 auto",
  marginBottom: "1rem"
};

export default ValueIntro;
