import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import ResultPage from "./ResultPage";

const questions = [
  "가족 (부부관계나 자녀양육 제외)",
  "부부관계/친밀한 관계",
  "부모됨/양육하기",
  "친구관계",
  "일",
  "자기 자신에 대한 교육/훈련",
  "휴식/즐거운 활동",
  "영성/초월성",
  "사회참여/시민의식",
  "자신을 신체적으로 돌보기 (운동, 수면, 식이 등)",
  "환경문제",
  "예술, 창조성 등"
];

function Container({ children }) {
  return (
    <div style={{
      maxWidth: '800px',
      margin: '2rem auto',
      padding: '2rem',
      backgroundColor: '#fff',
      borderRadius: '12px',
      boxShadow: '0 0 20px rgba(0,0,0,0.05)'
    }}>
      {children}
    </div>
  );
}

function QuestionSection({ title, step, responses, setResponses, onNext, onBack, disabledIndices = [] }) {
  const handleChange = (index, value) => {
    const updated = [...responses];
    updated[index] = parseInt(value);
    setResponses(updated);
  };

  const isImportance = step === 1;
  const labelLeft = isImportance ? "1 중요하지 않음" : "1 헌신하지 않음";
  const labelRight = isImportance ? "10 매우 중요" : "10 매우 헌신";

  return (
    <Container>
      <div style={{ backgroundColor: '#000', color: '#fff', padding: '0.7rem', textAlign: 'center', borderRadius: '8px', marginBottom: '2rem' }}>
        <h2 style={{ fontSize: '1.3rem', fontWeight: 600 }}>{title}</h2>
      </div>
      <ul style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
        {questions.map((q, idx) => {
          const isDisabled = disabledIndices.includes(idx);
          const allowNone = idx === 0 || idx === 2;

          return (
            <li key={idx} style={{ borderBottom: '1px solid #eee', paddingBottom: '1rem' }}>
              <div style={{ marginBottom: '0.5rem', fontWeight: 500, color: isDisabled ? '#999' : '#000' }}>{`${idx + 1}. ${q}`}</div>
              {isDisabled ? (
                <div style={{ fontStyle: 'italic', color: '#aaa' }}>해당 없음으로 선택된 항목입니다.</div>
              ) : (
                <div>
                  <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.3rem', fontSize: '0.8rem', color: '#888' }}>
                    <span>{labelLeft}</span>
                    <span>{labelRight}</span>
                  </div>
                  <div style={{ position: 'relative', height: '32px' }}>
                    <div style={{
                      position: 'absolute',
                      top: '14px',
                      left: 0,
                      right: 0,
                      height: '8px',
                      backgroundColor: '#eee',
                      borderRadius: '4px'
                    }} />
                    <div style={{
                      position: 'absolute',
                      top: '14px',
                      left: 0,
                      height: '8px',
                      width: `${((responses[idx] ?? 0) / 10) * 100}%`,
                      backgroundColor: '#aaa',
                      borderRadius: '4px',
                      transition: 'width 0.3s'
                    }} />
                    <div style={{ display: 'flex', justifyContent: 'space-between', position: 'relative', zIndex: 1 }}>
                      {[...Array(10)].map((_, i) => {
                        const score = i + 1;
                        return (
                          <label key={score} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', fontSize: '0.75rem', color: '#888' }}>
                            <input
                              type="radio"
                              name={`q-${idx}`}
                              value={score}
                              checked={responses[idx] === score}
                              onChange={() => handleChange(idx, score)}
                              style={{
                                marginBottom: '2px',
                                accentColor: '#000'
                              }}
                            />
                            ●
                          </label>
                        );
                      })}
                    </div>
                  </div>
                </div>
              )}
            </li>
          );
        })}
      </ul>
      <div style={{ marginTop: '3rem', textAlign: 'center' }}>
        {onBack && (
          <button
            onClick={onBack}
            style={{
              marginRight: '1rem',
              backgroundColor: '#fff',
              color: '#000',
              padding: '0.7rem 2rem',
              borderRadius: '8px',
              fontSize: '1rem',
              border: '1px solid #000'
            }}
          >
            이전
          </button>
        )}
        <button
          onClick={onNext}
          style={{
            backgroundColor: '#000',
            color: 'white',
            padding: '0.7rem 2rem',
            borderRadius: '8px',
            fontSize: '1rem'
          }}
        >
          다음
        </button>
      </div>
    </Container>
  );
}

function calculateResults(importance, commitment) {
  const validImportance = importance.map(v => v ?? 0);
  const validCommitment = commitment.map(v => v ?? 0);

  const scored = questions.map((q, i) => ({
    label: q,
    importance: validImportance[i],
    commitment: validCommitment[i],
    index: i
  }));

  const feedback1 = scored.filter(item => item.importance >= 9).sort((a, b) => b.importance - a.importance).map(item => item.label);
  const feedback2 = scored.filter(item => item.importance >= 9 && item.commitment <= 6).sort((a, b) => b.importance - a.importance).map(item => item.label);
  const score = scored.reduce((sum, val) => sum + val.importance * val.commitment, 0) / 12;

  return { feedback1, feedback2, score };
}

function App() {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [importance, setImportance] = useState(Array(12).fill(undefined));
  const [commitment, setCommitment] = useState(Array(12).fill(undefined));
  const [results, setResults] = useState(null);

  const handleNext = () => setStep(2);
  const handleSubmit = () => {
    const res = calculateResults(
      importance.map(v => v ?? 0),
      commitment.map(v => v ?? 0)
    );
    setResults(res);
    setStep(3);
  };

  const handleReset = () => {
    setImportance(Array(12).fill(undefined));
    setCommitment(Array(12).fill(undefined));
    setResults(null);
    setStep(1);
  };

  return (
    <div style={{ backgroundColor: '#f8f8f8', minHeight: '100vh', padding: '4rem 2rem' }}>
      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <h1 style={{ fontSize: '2.2rem', fontWeight: 'bold', color: '#333' }}>
          가치 명료화 설문
        </h1>
      </div>

      {step === 1 && (
        <QuestionSection
          title="내가 가치롭게 여기는 정도"
          step={step}
          responses={importance}
          setResponses={setImportance}
          onNext={handleNext}
          onBack={() => navigate("/valueintro")}
        />
      )}

      {step === 2 && (
        <QuestionSection
          title="내가 헌신(행동)하는 정도"
          step={step}
          responses={commitment}
          setResponses={setCommitment}
          onNext={handleSubmit}
          onBack={() => setStep(1)}
          disabledIndices={importance.map((v, i) => v === 0 && (i === 0 || i === 2) ? i : null).filter(i => i !== null)}
        />
      )}

      {step === 3 && results && (
        <ResultPage
          results={results}
          importance={importance}
          commitment={commitment}
          onReset={handleReset}
        />
      )}
    </div>
  );
}

export default App;
