import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import img1 from "./assets/onboarding1.png";
import img2 from "./assets/onboarding2.png";
import img3 from "./assets/onboarding3.png";

function OnboardingPage() {
  const navigate = useNavigate();
  const [step, setStep] = useState(0);

  const onboardingSteps = [
    {
      title: "밸류에 오신 걸 환영해요.",
      subtitle: "밸류는 당신의 내면을 들여다보고,\n마음속 소중한 것들을 떠올려보는 여정이에요.",
      image: img1,
      fullScreen: true,
      buttonText: "다음으로",
    },
    {
      title: "어떻게 진행되나요?",
      subtitle: "밸류는 전문가와 함께 혹은 혼자서\n당신에게 중요한 방향을 찾아보는 시간이랍니다.",
      image: img2,
      fullScreen: false,
      buttonText: "다음으로",
    },
    {
      title: "천천히, 당신의 세상으로",
      subtitle: "아무도 안 본다고 생각하고,\n솔직하게 써보세요.",
      image: img3,
      fullScreen: true,
      buttonText: "시작하기",
    }
  ];

  const current = onboardingSteps[step];

  const handleNext = () => {
    if (step < onboardingSteps.length - 1) {
      setStep(step + 1);
    } else {
      navigate("/consent");
    }
  };

  const handlePrev = () => {
    if (step > 0) setStep(step - 1);
  };

  const skip = () => navigate("/consent");

  return (
    <div
      style={{
        height: "100vh",
        width: "100%",
        position: "relative",
        overflow: "hidden",
        fontFamily: "sans-serif",
        backgroundColor: "transparent",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between"
      }}
    >
      {/* 배경 이미지 */}
      <img
        src={current.image}
        alt="background"
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: current.fullScreen ? "60vh" : "40vh",
          objectFit: "cover",
          zIndex: -1,
          opacity: 0.4
        }}
      />

      {/* SKIP */}
      <div
        style={{
          position: "absolute",
          top: "1.5rem",
          right: "1.5rem",
          color: "#666",
          fontSize: "0.9rem",
          cursor: "pointer",
          zIndex: 1
        }}
        onClick={skip}
      >
        SKIP &gt;
      </div>

      {/* 텍스트 + 인디케이터 */}
      <div style={{ padding: "2rem 1.5rem 0", textAlign: "center", zIndex: 1 }}>
        <h2 style={{ fontSize: "1.2rem", color: "#007BC7", marginBottom: "1rem" }}>
          {current.title}
        </h2>
        <p style={{ fontSize: "1rem", color: "#333", whiteSpace: "pre-line" }}>
          {current.subtitle}
        </p>
        <div style={{ marginTop: "1rem" }}>
          {onboardingSteps.map((_, i) => (
            <span
              key={i}
              style={{
                height: 8,
                width: 8,
                borderRadius: "50%",
                backgroundColor: i === step ? "#333" : "#ccc",
                display: "inline-block",
                margin: "0 4px"
              }}
            />
          ))}
        </div>
      </div>

      {/* 하단 버튼 영역 */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "1rem",
          padding: "2rem 1.5rem",
          zIndex: 1
        }}
      >
        {step > 0 && (
          <button
            onClick={handlePrev}
            style={{
              flex: 1,
              padding: "1rem",
              fontSize: "1rem",
              border: "1px solid #007BC7",
              borderRadius: "12px",
              background: "#fff",
              color: "#007BC7",
              cursor: "pointer"
            }}
          >
            이전으로
          </button>
        )}
        <button
          onClick={handleNext}
          style={{
            flex: 1,
            padding: "1rem",
            fontSize: "1rem",
            border: "none",
            borderRadius: "12px",
            background: "#007BC7",
            color: "white",
            cursor: "pointer"
          }}
        >
          {current.buttonText}
        </button>
      </div>
    </div>
  );
}

export default OnboardingPage;
