import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import img1 from "./assets/onboarding1.png";
import img2 from "./assets/onboarding2.png";
import img3 from "./assets/onboarding3.png";

function OnboardingPage() {
  const navigate = useNavigate();
  const [step, setStep] = useState(0);

  const onboardingSteps = [
    {
      image: img1,
      fullScreen: true,
      buttonText: "다음(1/3)",
    },
    {
      image: img2,
      fullScreen: false,
      title: "어떻게 진행되나요?",
      subtitle: "밸류는 전문가와 함께,\n당신에게 중요한 방향을 찾아보는 시간입니다.",
      subtext: "1. 내게 중요한 무언가를 골라보기\n2. 그 마음에 대해 글로 써보기\n3. 그걸 지켜갈 작은 실천 떠올리기",
      buttonText: "다음(2/3)",
    },
    {
      image: img3,
      fullScreen: true,
      buttonText: "시작하기(3/3)",
    }
  ];

  const current = onboardingSteps[step];

  const handlePrev = () => {
    if (step > 0) setStep(step - 1);
  };

  const handleNext = () => {
    if (step < onboardingSteps.length - 1) {
      setStep(step + 1);
    } else {
      navigate("/consent");
    }
  };

  const skip = () => navigate("/consent");

  return (
    <div
      style={{
        height: "100vh",
        width: "100%",
        overflow: "hidden",
        position: "relative",
        fontFamily: "sans-serif",
        backgroundImage: `url(${current.image})`,
        backgroundSize: current.fullScreen ? "cover" : "contain",
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center",
        display: "flex",
        flexDirection: "column",
        justifyContent: current.fullScreen ? "flex-end" : "flex-start",
        alignItems: "center",
        padding: "2rem",
        boxSizing: "border-box"
      }}
    >
      {/* SKIP */}
      <div style={{
        position: "absolute",
        top: "1rem",
        right: "1.5rem",
        color: "#888",
        fontSize: "0.9rem",
        zIndex: 2,
        cursor: "pointer"
      }} onClick={skip}>
        SKIP &gt;
      </div>

      {/* 텍스트 (2단계만 표시) */}
      {!current.fullScreen && (
        <div style={{
          backgroundColor: "rgba(255, 255, 255, 0.85)",
          padding: "1.5rem",
          borderRadius: "1rem",
          marginTop: "3rem",
          zIndex: 2
        }}>
          <h2 style={{ color: "#007BC7", fontSize: "1.2rem", marginBottom: "0.5rem", whiteSpace: "pre-line" }}>
            {current.title}
          </h2>
          <p style={{ fontSize: "1rem", color: "#111", whiteSpace: "pre-line", marginBottom: "1rem" }}>
            {current.subtitle}
          </p>
          <p style={{ fontSize: "0.9rem", color: "#666", whiteSpace: "pre-line" }}>
            {current.subtext}
          </p>
        </div>
      )}

      {/* 버튼 */}
      <div style={{
        display: "flex",
        justifyContent: "center",
        gap: "1rem",
        zIndex: 2,
        marginTop: current.fullScreen ? "auto" : "2rem"
      }}>
        {step > 0 && (
          <button
            onClick={handlePrev}
            style={{
              padding: "1rem 2rem",
              fontSize: "1rem",
              border: "1px solid #007BC7",
              borderRadius: "12px",
              background: "#fff",
              color: "#007BC7",
              cursor: "pointer"
            }}
          >
            이전으로
          </button>
        )}
        <button
          onClick={handleNext}
          style={{
            padding: "1rem 2rem",
            fontSize: "1rem",
            border: "none",
            borderRadius: "12px",
            background: "#007BC7",
            color: "white",
            cursor: "pointer"
          }}
        >
          {current.buttonText}
        </button>
      </div>
    </div>
  );
}

export default OnboardingPage;
