// Responsive layout update for iPhone 12 Pro view compatibility
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import ResultPage from "./ResultPage";
import MenuIcon from "@mui/icons-material/Menu";
import { IconButton } from "@mui/material";

const questions = [
  "가족 (부부관계나 자녀양육 제외)",
  "부부관계/친밀한 관계",
  "부모됨/양육하기",
  "친구관계",
  "일",
  "자기 자신에 대한 교육/훈련",
  "휴식/즐거운 활동",
  "영성/초월성",
  "사회참여/시민의식",
  "자신을 신체적으로 돌보기 (운동, 수면, 식이 등)",
  "환경문제",
  "예술, 창조성 등"
];

export function Header() {
  return (
    <div style={{
      display: 'flex', alignItems: 'center', justifyContent: 'space-between',
      padding: '1rem 1.2rem', borderBottom: '1px solid #ddd',
      position: 'sticky', top: 0, backgroundColor: '#fff', zIndex: 100,
      maxWidth: '500px', width: '100%', margin: '0 auto', boxSizing: 'border-box'
    }}>
      <div style={{ width: '32px' }}></div>
      <div style={{ fontSize: '1.2rem', fontWeight: 'bold', textAlign: 'center', flex: 1 }}>내 삶의 방향 찾기</div>
      <IconButton size="small">
        <MenuIcon />
      </IconButton>
    </div>
  );
}

export function ValueIntroTitle({ step }) {
  const titleText = step === 1 ? "내가 가치롭게 여기는 정도" : "내가 헌신(행동)하는 정도";
  const description = step === 1
    ? "사람마다 중요하게 여기는 가치가 다르며, 여기에 '정답'은 없습니다. 가장 중요하다고 생각되면 10, 중요하지 않다고 생각되면 1입니다."
    : "내가 중요하다고 여기는 만큼 행동하고 있는지를 1~10 사이 숫자로 표시해주세요. 헌신하지 않는다면 1, 매우 헌신하고 있다면 10입니다.";

  return (
    <div style={{
      backgroundColor: '#fff',
      maxWidth: '500px',
      margin: '0 auto',
      padding: '1rem 1.2rem 1.5rem',
      fontFamily: 'sans-serif',
      boxSizing: 'border-box'
    }}>
      <div style={{
        backgroundColor: '#f0f9ff',
        color: '#1292EE',
        fontWeight: 'bold',
        textAlign: 'center',
        borderRadius: '16px',
        padding: '0.8rem 1rem',
        fontSize: '1rem',
        border: '1px solid #cce9ff',
        marginBottom: '0.8rem'
      }}>{titleText}</div>

      <p style={{ fontSize: '0.85rem', color: '#90a4ae', textAlign: 'center', lineHeight: '1.5' }}>{description}</p>
    </div>
  );
}

function QuestionSection({ step, responses, setResponses, onNext, onBack, disabledIndices = [] }) {
  const handleChange = (index, value) => {
    const updated = [...responses];
    updated[index] = parseInt(value);
    setResponses(updated);
  };

  const isImportance = step === 1;
  const labelLeft = isImportance ? "1 중요하지 않음" : "1 헌신하지 않음";
  const labelRight = isImportance ? "10 매우 중요" : "10 매우 헌신";

  return (
    <div style={{
      backgroundColor: '#fff',
      maxWidth: '500px',
      margin: '0 auto',
      padding: '1.5rem 1.2rem 4rem',
      fontFamily: 'sans-serif',
      boxSizing: 'border-box'
    }}>
      <ul style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
        {questions.map((q, idx) => {
          const isDisabled = disabledIndices.includes(idx);
          return (
            <li key={idx} style={{ borderBottom: '1px solid #eee', paddingBottom: '1rem' }}>
              <div style={{ marginBottom: '0.5rem', fontWeight: 500, color: isDisabled ? '#999' : '#000' }}>{`${idx + 1}. ${q}`}</div>
              {isDisabled ? (
                <div style={{ fontStyle: 'italic', color: '#aaa' }}>해당 없음으로 선택된 항목입니다.</div>
              ) : (
                <>
                  <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '0.75rem', color: '#888' }}>
                    <span>{labelLeft}</span><span>{labelRight}</span>
                  </div>
                  <div style={{ position: 'relative', height: '20px', margin: '1rem 0' }}>
                    <div style={{
                      position: 'absolute',
                      top: '50%',
                      left: 0,
                      width: '100%',
                      height: '8px',
                      backgroundColor: '#eee',
                      borderRadius: '4px',
                      transform: 'translateY(-50%)'
                    }}></div>
                    <div style={{
                      position: 'absolute',
                      top: '50%',
                      left: 0,
                      width: `${(responses[idx] ?? 0) * 10}%`,
                      height: '8px',
                      backgroundColor: '#1292EE',
                      borderRadius: '4px',
                      transform: 'translateY(-50%)',
                      transition: 'width 0.3s'
                    }}></div>
                  </div>
                  <div style={{ display: 'flex', justifyContent: 'space-between', padding: '0 0.2rem' }}>
                    {[...Array(10)].map((_, i) => {
                      const score = i + 1;
                      return (
                        <label key={score} style={{ position: 'relative' }}>
                          {responses[idx] === score && (
                            <div style={{
                              position: 'absolute',
                              top: '-30px',
                              left: '50%',
                              transform: 'translateX(-50%)',
                              backgroundColor: '#1292EE',
                              color: '#fff',
                              padding: '0.2rem 0.5rem',
                              borderRadius: '8px',
                              fontSize: '0.75rem'
                            }}>{score}</div>
                          )}
                          <input
                            type="radio"
                            name={`q-${idx}`}
                            value={score}
                            checked={responses[idx] === score}
                            onChange={() => handleChange(idx, score)}
                            style={{ margin: '0 4px' }}
                          />
                        </label>
                      );
                    })}
                  </div>
                </>
              )}
            </li>
          );
        })}
      </ul>

      <div style={{ marginTop: '3rem', textAlign: 'center' }}>
        {onBack && (
          <button
            onClick={onBack}
            style={{
              marginRight: '1rem',
              backgroundColor: '#fff',
              color: '#1292EE',
              padding: '0.6rem 1.8rem',
              borderRadius: '8px',
              fontSize: '1rem',
              border: '1px solid #1292EE'
            }}>
            이전
          </button>
        )}
        <button
          onClick={onNext}
          style={{
            backgroundColor: '#1292EE',
            color: 'white',
            padding: '0.6rem 1.8rem',
            borderRadius: '8px',
            fontSize: '1rem',
            border: 'none'
          }}>
          다음
        </button>
      </div>
    </div>
  );
}

// export default QuestionSection;

export default SurveyFlow;
