import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const questions = [
  "가족 (부부관계나 자녀양육 제외)",
  "부부관계/친밀한 관계",
  "부모됨/양육하기",
  "친구관계",
  "일",
  "자기 자신에 대한 교육/훈련",
  "휴식/즐거운 활동",
  "영성/초월성",
  "사회참여/시민의식",
  "자신을 신체적으로 돌보기 (운동, 수면, 식이 등)",
  "환경문제",
  "예술, 창조성 등"
];

function calculateResults(importance, commitment) {
  const scored = questions.map((q, i) => ({
    label: q,
    importance: importance[i] ?? 0,
    commitment: commitment[i] ?? 0,
  }));

  const feedback1 = scored
    .filter(item => item.importance >= 9)
    .sort((a, b) => b.importance - a.importance)
    .map(item => item.label);

  const feedback2 = scored
    .filter(item => item.importance >= 9 && item.commitment <= 6)
    .sort((a, b) => b.importance - a.importance)
    .map(item => item.label);

  const score =
    scored.reduce((sum, val) => sum + val.importance * val.commitment, 0) / 12;

  return { feedback1, feedback2, score };
}

function SurveyFlow() {
  const navigate = useNavigate();
  const [step, setStep] = useState(1);
  const [importance, setImportance] = useState(Array(12).fill(undefined));
  const [commitment, setCommitment] = useState(Array(12).fill(undefined));

  const handleNext = () => {
    setStep(2);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const handleSubmit = () => {
    const results = calculateResults(importance, commitment);
    navigate("/result", {
      state: {
        results,
        importance,
        commitment,
        name: "사용자" // 입력값 있다면 여기에 반영
      }
    });
  };

  return (
    <div style={{ backgroundColor: '#f8f8f8', minHeight: '100vh', padding: '4rem 2rem' }}>
      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <h1 style={{ fontSize: '2.2rem', fontWeight: 'bold', color: '#333' }}>
          가치 명료화 설문
        </h1>
      </div>

      {step === 1 && (
        <QuestionSection
          title="내가 가치롭게 여기는 정도"
          step={step}
          responses={importance}
          setResponses={setImportance}
          onNext={handleNext}
          onBack={() => navigate("/valueintro")}
        />
      )}

      {step === 2 && (
        <QuestionSection
          title="내가 헌신(행동)하는 정도"
          step={step}
          responses={commitment}
          setResponses={setCommitment}
          onNext={handleSubmit}
          onBack={() => setStep(1)}
          disabledIndices={importance.map((v, i) => v === 0 && (i === 0 || i === 2) ? i : null).filter(i => i !== null)}
        />
      )}
    </div>
  );
}

export default SurveyFlow;
