import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function ValueIntroFlow() {
  const navigate = useNavigate();
  const [step, setStep] = useState(0);

  const pages = [
    {
      image: "가치소개 이미지1",
      lines: [
        "다음에 제시되는 내용은",
        "일부 사람들이 중요하다고 여기는",
        "삶의 영역입니다.",
        "",
        "모든 사람들이 동일한 가치를 가지고 있지 않으며,",
        "이 워크시트로 당신이 올바른 가치를 지니고 있는지",
        "확인하려고 하는 것도 아닙니다."
      ],
      skip: true
    },
    {
      image: "가치소개 이미지2",
      lines: [
        "‘가치’란",
        "우리가 세상, 타인, 그리고 자기 자신과",
        "어떻게 관계 맺기를 원하는지를 향한",
        "마음 깊은 바람입니다.",
        "",
        "가치는 우리 삶을 이끄는 원칙이자,",
        "삶을 살아가는 데",
        "동기를 부여해주는 방향성입니다."
      ],
      skip: true
    },
    {
      image: "가치소개 이미지3",
      lines: [
        "‘아무도’ 이 워크시트를 읽지 않는다고",
        "생각하시고 당신의 가치를 찾아 보세요."
      ],
      final: true
    }
  ];

  const current = pages[step];

  const handleNext = () => {
    if (step + 1 < pages.length) {
      setStep(step + 1);
    } else {
      navigate("/survey");
    }
  };

  const handleSkip = () => {
    navigate("/survey");
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif", minHeight: "100vh", display: "flex", flexDirection: "column", justifyContent: "space-between" }}>
      <div>
        <h2 style={{ textAlign: "center", marginBottom: "2rem" }}>내 삶의 방향 찾기</h2>

        <div style={{
          width: "100%",
          height: "240px",
          backgroundColor: "#eee",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          marginBottom: "2rem",
          color: "#aaa"
        }}>
          {current.image}
        </div>

        <div style={{ textAlign: "center", color: "#333", whiteSpace: "pre-line", fontSize: "1rem", lineHeight: "1.6" }}>
          {current.lines.map((line, idx) => (
            <p key={idx} style={{ margin: 0, fontWeight: idx === 0 && step === 1 ? "bold" : "normal" }}>{line}</p>
          ))}
        </div>
      </div>

      <div style={{ display: "flex", justifyContent: current.skip ? "space-between" : "center", marginTop: "3rem" }}>
        {current.skip && (
          <button onClick={handleSkip} style={whiteButton}>
            건너뛰기
          </button>
        )}
        <button onClick={handleNext} style={blackButton}>
          {current.final ? "시작할게요" : "다음으로"}
        </button>
      </div>
    </div>
  );
}

const whiteButton = {
  padding: "0.9rem 2rem",
  border: "1px solid black",
  borderRadius: "12px",
  backgroundColor: "white",
  color: "black",
  fontSize: "1rem",
  cursor: "pointer"
};

const blackButton = {
  padding: "0.9rem 2rem",
  border: "none",
  borderRadius: "12px",
  backgroundColor: "black",
  color: "white",
  fontSize: "1rem",
  cursor: "pointer"
};

export default ValueIntroFlow;
