import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import img1 from "./assets/onboarding1.png";
import img2 from "./assets/onboarding3.png";

function OnboardingPage() {
  const navigate = useNavigate();
  const [step, setStep] = useState(0);

  const onboardingSteps = [
    {
      title: "밸류에 오신 걸 환영해요.",
      subtitle: "밸류는 당신의 내면을 들여다보고,\n마음속 소중한 것들을 떠올려보는 여정이에요.",
      subtext: "(꼭 ‘올바른 답’을 찾지 않아도 괜찮아요.\n이건 ‘정답을 맞히는 시간’이 아니라\n당신이 어디쯤 있는지 잠시 멈춰서 바라보는 일이니까요.)",
      image: img1,
      buttonText: "다음(1/3)",
    },
    {
      title: "어떻게 진행되나요?",
      subtitle: "밸류는 전문가와 함께,\n당신에게 중요한 방향을 찾아보는 시간입니다.",
      subtext: "1. 내게 중요한 무언가를 골라보기\n2. 그 마음에 대해 글로 써보기\n3. 그걸 지켜갈 작은 실천 떠올리기",
      image: img2,
      buttonText: "다음(2/3)",
    },
    {
      title: "천천히, 당신의 세상으로",
      subtitle: "방향이 없다면, 지금 흘러가는 대로 가게 돼요.\n이 시간엔 당신이 정말 소중히 여기는 것을\n잠시 멈춰 바라봐 주세요.",
      subtext: "(아무도 안 본다고 생각하고, 솔직하게 써보세요.)",
      image: img3,
      buttonText: "확인(3/3)",
    }
  ];

  const current = onboardingSteps[step];

  const handlePrev = () => {
    if (step > 0) setStep(step - 1);
  };

  const handleNext = () => {
    if (step < onboardingSteps.length - 1) {
      setStep(step + 1);
    } else {
      navigate("/consent");
    }
  };

  const skip = () => navigate("/consent");

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif", position: "relative", minHeight: "100vh" }}>
      {/* SKIP */}
      <div style={{ position: "absolute", top: "1rem", right: "1.5rem", color: "#888", fontSize: "0.9rem", cursor: "pointer" }} onClick={skip}>
        SKIP &gt;
      </div>

      {/* 타이틀 */}
      <h2 style={{ fontWeight: "bold", fontSize: "1.5rem", marginTop: "3rem", whiteSpace: "pre-line", color: "#007BC7" }}>
        {current.title}
      </h2>
      <p style={{ margin: "1rem 0", fontSize: "1rem", lineHeight: "1.5", whiteSpace: "pre-line", color: "#111" }}>
        {current.subtitle}
      </p>

      {/* 이미지 */}
      <div style={{ display: "flex", justifyContent: "center", margin: "2rem 0" }}>
        <img src={current.image} alt="onboarding step" style={{ width: "100%", maxWidth: "320px", height: "auto" }} />
      </div>

      {/* 설명 텍스트 */}
      <p style={{
        fontSize: "0.85rem",
        color: "#666",
        textAlign: "center",
        lineHeight: "1.5",
        whiteSpace: "pre-line"
      }}>
        {current.subtext}
      </p>

      {/* 하단 버튼 */}
      <div style={{
        position: "fixed",
        bottom: "2rem",
        left: 0,
        width: "100%",
        display: "flex",
        justifyContent: "center",
        gap: "1rem"
      }}>
        {step > 0 && (
          <button
            onClick={handlePrev}
            style={{
              padding: "1rem 2rem",
              fontSize: "1rem",
              border: "1px solid #007BC7",
              borderRadius: "12px",
              background: "#fff",
              color: "#007BC7",
              cursor: "pointer"
            }}
          >
            이전으로
          </button>
        )}
        <button
          onClick={handleNext}
          style={{
            padding: "1rem 2rem",
            fontSize: "1rem",
            border: "none",
            borderRadius: "12px",
            background: "#007BC7",
            color: "white",
            cursor: "pointer"
          }}
        >
          {current.buttonText}
        </button>
      </div>
    </div>
  );
}

export default OnboardingPage;
