import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function ModeSelectionFlow() {
  const navigate = useNavigate();
  const [step, setStep] = useState(0);
  const [form, setForm] = useState({
    name: "",
    gender: "",
    age: "",
    job: "",
    emotion: ""
  });

  const inputStyle = {
    width: "100%",
    padding: "1rem",
    marginBottom: "1rem",
    backgroundColor: "#f0f0f0",
    border: "none",
    borderRadius: "8px"
  };

  const handleChange = (field, value) => {
    setForm(prev => ({ ...prev, [field]: value }));
  };

  const steps = [
    {
      label: "참가 방식",
      content: (
        <>
          <h2>참가 방식 선택</h2>
          <p>밸류는 그룹/개인이 모두 가능한 워크숍이에요.<br />참가 방식을 선택해 주세요.</p>
          <div style={{ backgroundColor: "#eee", padding: "2rem", margin: "1rem 0", color: "#999", textAlign: "center" }}>개인 (비활성화)</div>
          <div style={{ backgroundColor: "#eee", padding: "2rem", marginBottom: "2rem", textAlign: "center" }}>그룹</div>
        </>
      )
    },
    {
      label: "기본정보",
      content: (
        <>
          <h2>기본 정보 입력</h2>
          <p>이름은 필수이며, 나머지 항목은 선택입니다.<br />더 구체적으로 입력할수록 결과물이 더 정교해져요.</p>
          <input placeholder="이름 *" required value={form.name} onChange={e => handleChange("name", e.target.value)} style={inputStyle} />
          <input placeholder="성별" value={form.gender} onChange={e => handleChange("gender", e.target.value)} style={inputStyle} />
          <input placeholder="나이" value={form.age} onChange={e => handleChange("age", e.target.value)} style={inputStyle} />
          <input placeholder="직업" value={form.job} onChange={e => handleChange("job", e.target.value)} style={inputStyle} />
        </>
      )
    },
    {
      label: "감정상태",
      content: (
        <>
          <h2>감정 상태 선택</h2>
          <p>지난 1개월 동안,<br />가장 가까운 상태의 문장을 골라주세요.</p>
          {[
            "기운이 없고, 예전처럼 즐거움을 느끼지 못해요. 마음이 자꾸 가라앉고, 무의미하게 느껴져요.",
            "자꾸 걱정이 떠오르고, 긴장된 마음이 쉽게 가라앉지 않아요. 앞으로가 막막하게 느껴지고, 쉽게 놀라요.",
            "생각이 너무 많고, 별일 아닌 일에도 감정이 쉽게 흔들려요. 피곤한데도 쉬는 기분이 들지 않아요."
          ].map((text, idx) => (
            <div key={idx}
              onClick={() => handleChange("emotion", text)}
              style={{
                backgroundColor: "#eee",
                padding: "1.5rem",
                margin: "1rem 0",
                cursor: "pointer",
                border: form.emotion === text ? "2px solid black" : "none"
              }}
            >
              “{text}”
            </div>
          ))}
        </>
      )
    }
  ];

  const progressHeader = (
    <div style={{ display: "flex", justifyContent: "center", gap: "2rem", marginBottom: "1.5rem" }}>
      {steps.map((s, idx) => (
        <div key={idx} style={{ fontWeight: step === idx ? "bold" : "normal", color: step === idx ? "black" : "#ccc" }}>
          {step + 1 === idx + 1 ? `● ${s.label}` : `${idx + 1} ${s.label}`}
        </div>
      ))}
    </div>
  );

  const canGoNext = () => {
    if (step === 1) return form.name.trim() !== "";
    return true;
  };

  return (
    <div style={{ padding: "2rem", maxWidth: "500px", margin: "0 auto" }}>
      {progressHeader}
      {steps[step].content}
      <div style={{ marginTop: "2rem", display: "flex", justifyContent: "space-between" }}>
        {step > 0 && (
          <button onClick={() => setStep(step - 1)} style={whiteButton}>이전</button>
        )}
        <button
          onClick={() => step < steps.length - 1 ? setStep(step + 1) : navigate("/survey")}
          style={blackButton}
          disabled={step === 1 && !canGoNext()}
        >
          다음
        </button>
      </div>
    </div>
  );
}

const whiteButton = {
  padding: "0.8rem 2rem",
  border: "1px solid black",
  borderRadius: "12px",
  backgroundColor: "white",
  color: "black",
  fontSize: "1rem",
  cursor: "pointer"
};

const blackButton = {
  padding: "0.8rem 2rem",
  border: "none",
  borderRadius: "12px",
  backgroundColor: "black",
  color: "white",
  fontSize: "1rem",
  cursor: "pointer"
};

export default ModeSelectionFlow;
